gameforge.collect_quest_lv30._330_npcChat = "Das Geheimrezept "
gameforge.collect_quest_lv30._340_sayTitle = "Questrolle: "
gameforge.collect_quest_lv30._350_say = "Sieh mal an, ist das ein Geheimrezept von[ENTER]Chaegirab, dem Biologen? Hm, das wird deine[ENTER]Halbmenschenstärke um 20 Punkte erhöhen.[ENTER]Hier ist dein Trank![ENTER]Außerdem soll ich dir noch diesen Ring geben. Er[ENTER]sieht wertvoll aus, also verliere ihn nicht! "
