gameforge.main_quest_lv92._590_say = "Se si uccide un drago nel momento in cui il suo[ENTER]comprendonio č offuscato, questi sputerŕ la perla[ENTER]della notte. Grazie a questa perla sarŕ possibile[ENTER]evocare i poteri incommensurabili di Sung Mahis[ENTER]nel nostro mondo.[ENTER]Esattamente in ciň consiste il loro piano oscuro. "
gameforge.main_quest_lv92._600_say = "I nostri prossimi passi dovranno essere molto[ENTER]ponderati. Quando avrň novitŕ che ti riguardano ti[ENTER]manderň a chiamare. Grazie di tutto. "
gameforge.main_quest_lv92._610_say = "L'avventura continuerŕ non appena avrai raggiunto[ENTER]il livello 93. "
gameforge.main_quest_lv93 = {}
gameforge.main_quest_lv93._010_sendLetter = "Ricarica forza I "
gameforge.main_quest_lv93._020_say = "L'Ammiraglio Angmur vuole parlare con te. "
gameforge.main_quest_lv93._030_say = "Siamo alle calcagna dei Servitori dell'Oscuritŕ. "
gameforge.main_quest_lv93._040_say = "Per poterli sconfiggere č necessario aumentare i[ENTER]tuoi poteri.[ENTER]Conosci la Luce interiore? Si tratta di energia[ENTER]concentrata. Questa si trova in alcune creature[ENTER]mitiche o in alcune piante. Camminatore Yon-I[ENTER]riesce a creare da essa un energizzante. Vai[ENTER]subito da lui. "
gameforge.main_quest_lv93._050_say = "D'accordo. "
gameforge.main_quest_lv93._060_say = "Ah, ecco il mio salvatore![ENTER]La Luce interiore, oh sě... Il mio energizzante[ENTER]farŕ di te un avversario degno dei Servitori[ENTER]dell'Oscuritŕ. "
gameforge.main_quest_lv93._070_say = "Per questo motivo ho bisogno della Luce interiore[ENTER]dei Ciclopi e dei Giganti. Solo i rispettivi capi[ENTER]tribů la possiedono. Occupati prima dei Ciclopi. "
gameforge.main_quest_lv93._080_say = "D'accordo. "
gameforge.main_quest_lv93._090_sayTitle = "La Luce interiore I "
gameforge.main_quest_lv93._100_say = "Sconfiggi Polifemo, Re dei Ciclopi, e rubagli la[ENTER]Luce interiore. "
gameforge.main_quest_lv93._110_say = "Vieni a mani vuote? In questo modo non potrň[ENTER]aiutarti. Portami la Luce interiore! "
gameforge.main_quest_lv93._120_1_notice = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._130_say = "Parla con Camminatore Yon-I. "
gameforge.main_quest_lv93._140_say = "Cosě questa sarebbe la Luce interiore... prima che[ENTER]io possa lavorarla deve essere purificata. Ci vorrŕ[ENTER]del tempo. Ti faccio sapere. "
gameforge.main_quest_lv93._150_sendLetter = "Ricarica forza II "
gameforge.main_quest_lv93._160_say = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._170_say = "Bene, la Luce interiore di Polifemo č purificata.[ENTER]Ora abbiamo bisogno di quella dei Giganti. Per[ENTER]questo dovrai sconfiggere il Re Wobba. "
gameforge.main_quest_lv93._180_say = "Mi avvio subito. "
gameforge.main_quest_lv93._190_sayTitle = "La Luce interiore II "
gameforge.main_quest_lv93._200_say = "Sconfiggi il Re Wobba e sottraigli la Luce interiore. "
gameforge.main_quest_lv93._210_say = "Vieni a mani vuote? Mi stupirebbe parecchio se tu[ENTER]non fossi all'altezza di questo compito. Su,[ENTER]avviati e riprova! "
gameforge.main_quest_lv93._220_1_notice = "Hai in mano la Luce interiore dei Giganti. "
gameforge.main_quest_lv93._220_2_notice = "Portala a Camminatore Yon-I. "
gameforge.main_quest_lv93._230_say = "Parla con Camminatore Yon-I. "
gameforge.main_quest_lv93._240_say = "La Luce interiore del Re Wobba possiede degli[ENTER]enormi poteri. Credo che ciň sia causato dalla mole.[ENTER]Ora devo pulirla e mi ci vorrŕ del tempo. "
gameforge.main_quest_lv93._250_sendLetter = "Ricarica forza III "
gameforge.main_quest_lv93._260_say = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._270_say = "Molto bene, sei arrivato. Ho bisogno del tuo aiuto.[ENTER]Affinché il mio elisir energizzante possa avere la[ENTER]massima efficacia ho bisogno di un ulteriore[ENTER]componente: l'Essenza millenium. "
gameforge.main_quest_lv93._280_say = "Essenza millenium? Non ne ho mai sentito parlare. "
gameforge.main_quest_lv93._290_say = "Questa č una miscela efficacissima generata[ENTER]dall'essenza di varie erbe.[ENTER]Conosci Octavio? Vive sulla terraferma ed č l'unico[ENTER]in grado di produrre l'Essenza millenium. Vai da[ENTER]lui. Spero ci possa essere d'aiuto. A volte č un[ENTER]po' cocciuto... "
gameforge.main_quest_lv93._300_say = "D'accordo. "
gameforge.main_quest_lv93._310_sayTitle = "Parla con Octavio! "
gameforge.main_quest_lv93._320_say = "Cerca Octavio. Vive sulla terraferma ed č l'unico[ENTER]in grado di distillare l'Essenza millenium. "
gameforge.main_quest_lv93._330_say = "Ciao! Cosa ti posso portare? Un Samgyetang magari?[ENTER]La specialitŕ della casa con... "
gameforge.main_quest_lv93._340_say = "Octavio, non sono qui per gustare le specialitŕ[ENTER]della tua cucina, ho bisogno di qualcos'altro: mi[ENTER]occorre l'Essenza millenium. "
gameforge.main_quest_lv93._350_say = "Ohi, sssshhh, piano! L'Essenza millenium...[ENTER]No, non č possibile. Chi te l'ha detto? Solo in[ENTER]pochi sanno davvero usare il potere che l'essenza[ENTER]dona. Non voglio correre nessun rischio. E ora[ENTER]vai, prima che ti senta qualcuno! "
gameforge.main_quest_lv93._360_say = "Octavio, aspetta un attimo e ascoltami! Mi manda[ENTER]Camminatore Yon-I dalla Roccia del Drago Scuro.[ENTER]Abbiamo necessitŕ assoluta dell'essenza. Senza di[ENTER]essa non abbiamo nessuna chance contro i Servitori[ENTER]dell'Oscuritŕ. Il mondo, cosě come lo vediamo,  č[ENTER]destinato al declino. Solo tu puoi produrla. Ti prego! "
gameforge.main_quest_lv93._370_say = "Hai detto Camminatore Yon-I? Hm, č un vecchio[ENTER]amico. Abbiamo fatto tante piacevoli chiacchierate[ENTER]accompagnate da ottimi vini.[ENTER]Devo pensarci su. Torna domani e ti darň una[ENTER]risposta. "
gameforge.main_quest_lv93._380_say = "Intesi, tornerň domani. "
gameforge.main_quest_lv93._390_say = "Non ho ancora preso una decisione.[ENTER]Torna piů tardi. "
gameforge.main_quest_lv93._400_npcChat = "L'Essenza millenium "
gameforge.main_quest_lv93._410_say = "Octavio vorrebbe parlare con te. "
gameforge.main_quest_lv93._420_say = "Dunque, ho pensato per tutta la notte alla tua[ENTER]richiesta. Corro un grosso rischio, ma so di fare[ENTER]la cosa giusta.[ENTER]Sě, ti aiuterň. "
gameforge.main_quest_lv93._430_say = "Grandioso! "
gameforge.main_quest_lv93._440_say = "Tuttavia, al momento non ho l'essenza a disposizione.[ENTER]Potresti procurarmi qualche ingrediente? Cosě sarň[ENTER]in grado produrti l'essenza. "
gameforge.main_quest_lv93._450_say = "Certamente. Di cosa hai bisogno? "
gameforge.main_quest_lv93._460_say = "Mi mancano Radici di gango, Funghi tue e Rose alpine.[ENTER]Mi occorrono 200 pezzi di ciascun ingrediente. "
gameforge.main_quest_lv93._470_say = "Mi avvio immediatamente. Dove posso trovare il tutto? "
gameforge.main_quest_lv93._480_say = "Trovi queste piante in diversi luoghi del continente.[ENTER]Mentre tu sarai via, io inizierň con la preparazione. "
gameforge.main_quest_lv93._490_sayTitle = "La preparazione della ricetta millenium "
gameforge.main_quest_lv93._500_say = "Cerca Radici di gango, funghi tue e Rose alpine.[ENTER]Ti occorrono 200 pezzi per ciascuno. "
gameforge.main_quest_lv93._510 = "Trovi queste piante in diversi luoghi del continente.[ENTER]Mentre tu sarai via, io inizierň con la preparazione. "
gameforge.main_quest_lv93._520_say = "Ah, molto bene, sei giŕ di ritorno e hai tutto con[ENTER]te. Mi metto subito al lavoro.[ENTER]A questo punto, perň, devo restare assolutamente[ENTER]indisturbato. Torna domani alla stessa ora.[ENTER]Per allora l'essenza dovrebbe essere pronta. "
gameforge.main_quest_lv93._530_say = "Perfetto, a domani. "
gameforge.main_quest_lv93._540_say = "Capisco che tu abbia fretta. Tuttavia, devi avere[ENTER]ancora un po' di pazienza. Torna piů tardi. "
gameforge.main_quest_lv93._550_say = "Octavio ti cerca. "
gameforge.main_quest_lv93._560_say = "Ah, arrivi proprio al momento giusto: l'Essenza[ENTER]millenium č pronta! Puoi portarla a Camminatore[ENTER]Yon-I. "
gameforge.main_quest_lv93._570_say = "Ti ringrazio. "
gameforge.main_quest_lv93._580_sendLetter = "Essenza millenium "
gameforge.main_quest_lv93._590_say = "Camminatore Yon-I ti cerca. "
gameforge.main_quest_lv93._600_say = "Hai l'Essenza millenium con te![ENTER]Ora ho tutti gli ingredienti per il mio elisir[ENTER]energizzante. Ti chiamo appena sarŕ pronto. "
gameforge.main_quest_lv93._610_say = "Benissimo. "
gameforge.main_quest_lv93._620_say = "L'avventura continua non appena avrai raggiunto il[ENTER]livello 94. "
gameforge.main_quest_lv93._630_say = "%s: %d pz, %s: %d pz, %s: %d pz "
gameforge.main_quest_lv93._640_say = "Ho una locanda da gestire e non ho troppo tempo a[ENTER]disposizione. Portami tutti gli ingredienti che mi[ENTER]mancano e sii veloce. "
gameforge.main_quest_lv93._650_1_notice = "Hai trovato tutti gli ingredienti necessari per la[ENTER]produzione dell'Essenza millenium. "
gameforge.main_quest_lv93._650_2_notice = "Torna da Octavio. "
