gameforge.subquest_32._440_sayTitle = "Mercante ambulante: "
gameforge.subquest_32._450_say = "Hai preso tutto? Fammi vedere...[ENTER]Si, c'č proprio tutto. Grazie a te potrň[ENTER]allargare il mio giro d'affari. Ecco la tua[ENTER]ricompensa. Divertiti![ENTER]Venite gente! C'č merce per tutti! "
