gameforge.locale.monster_chat_1105_1 = "Zima jest super. "
gameforge.locale.monster_chat_1105_2 = "Góra Sohan jest najlepsza. "
gameforge.locale.monster_chat_1105_3 = "Co za piękna pogoda. "
gameforge.locale.monster_chat_1105_4 = "Będziesz taki sam jak ja. "
gameforge.locale.monster_chat_1105_5 = "Możesz żyć wiecznie... przynajmniej do ocieplenia się klimatu... "
gameforge.locale.monster_chat_1105_6 = "Jak myślisz, ile czasu musi minąć abyś w końcu zamarzł? "
gameforge.locale.monster_chat_1106_1 = "Ludzie... tutaj?! "
gameforge.locale.monster_chat_1106_2 = "Jestem bardzo zmęczony... ale i tak Cię pokonam! "
gameforge.locale.monster_chat_1106_3 = "Czy ktoś mnie słyszy? "
gameforge.locale.monster_chat_1106_4 = "Nie pochodzisz stąd! "
gameforge.locale.monster_chat_1106_5 = "Lepiej się wycofaj! "
gameforge.locale.monster_chat_1106_6 = "Obcy... taki samotny? "
gameforge.locale.monster_chat_1191_1 = "Zobaczymy, może zjawi się jakiś przystojniak. "
gameforge.locale.monster_chat_1191_2 = "Tak mi zimno i smutno. Gdzie jest mój przyszły mąż? "
gameforge.locale.monster_chat_1191_3 = "Powinnam nad nim popracować? "
gameforge.locale.monster_chat_1191_4 = " "
gameforge.locale.monster_chat_1191_5 = "Czegoś brakuje, nie jestem szczęśliwa... "
gameforge.locale.monster_chat_1191_6 = "Odbiorę Ci życie! "
gameforge.locale.monster_chat_1301_1 = "To człowiek...! "
gameforge.locale.monster_chat_1301_2 = "Zaraz sprawię, że twoja twarz stanie się jeszcze bladsza! "
gameforge.locale.monster_chat_1301_3 = "Chcesz mnie wyzwać na pojedynek? Zgadzam się! "
gameforge.locale.monster_chat_1301_4 = "Zaatakowałeś mnie! Kwik! Nie daruję Ci tego! "
gameforge.locale.monster_chat_1301_5 = "Słaby, słaby człowiek! "
gameforge.locale.monster_chat_1301_6 = "Zobaczmy ile jesteś w stanie znieść! "
gameforge.locale.monster_chat_1302_1 = "Ucieczka? Nawet o tym nie myśl! "
gameforge.locale.monster_chat_1302_2 = "Taaa... podejdź bliżej... KWIK! "
gameforge.locale.monster_chat_1302_3 = "Miecz drży w mojej ręce. Kwik!! "
gameforge.locale.monster_chat_1302_4 = "Nie przeżyjesz tej walki! "
gameforge.locale.monster_chat_1302_5 = "Nadchodzi mój miecz! "
gameforge.locale.monster_chat_1302_6 = "Bolesne, prawda? (śmiech) "
gameforge.locale.monster_chat_1303_1 = "Niebezpieczeństwo może czaić się nawet w trawie! "
gameforge.locale.monster_chat_1303_2 = "Uważaj na trawę! "
gameforge.locale.monster_chat_1303_3 = "Dobra. Chodź do mnie. (śmiech) "
gameforge.locale.monster_chat_1303_4 = "Jak śmiesz do mnie podchodzić?! Musisz ponieść konsekwencje! "
gameforge.locale.monster_chat_1303_5 = "Giń! Giń! "
gameforge.locale.monster_chat_1303_6 = "Żałujesz, że tu przyszedłeś? "
gameforge.locale.monster_chat_1305_1 = "Kto mnie pokona? "
gameforge.locale.monster_chat_1305_2 = "Ty?! Ty chcesz walczyć? "
gameforge.locale.monster_chat_1305_3 = "Podejdź tutaj jeśli chcesz walczyć! "
gameforge.locale.monster_chat_1305_4 = "Chyba nie myślisz o ucieczce, prawda? "
gameforge.locale.monster_chat_1305_5 = "Nie doceniasz potęgi mojego miecza. "
gameforge.locale.monster_chat_1305_6 = "Boisz się? (śmiech) "
gameforge.locale.monster_chat_1901_1 = "Myślę, że dzisiaj umrzesz na moich rękach. "
gameforge.locale.monster_chat_1901_2 = "Oh, lubię Cię... "
gameforge.locale.monster_chat_1901_3 = "No to jak będzie? Mój kochany obiedzie... "
gameforge.locale.monster_chat_1901_4 = "Hey! Uciekanie jak zwykły tchórz Ci nie pomoże. "
gameforge.locale.monster_chat_1901_5 = "Jesteś już pod wpływem mojego zaklęcia? Hmm? "
gameforge.locale.monster_chat_1901_6 = "Czy nie chcesz troszkę pokrzyczeć? (śmiech) "
gameforge.locale.monster_chat_20001_1 = "Chyba jesteś lekko zdenerwowany... nie bój się, nie powinno nic wybuchnąć. "
gameforge.locale.monster_chat_20001_2 = "Czasami mam tego wszystkiego dość... "
gameforge.locale.monster_chat_20001_3 = "Nuuuuuuuda. Moje wnuki już nawet mnie nie odwiedzają. "
gameforge.locale.monster_chat_20001_4 = "Kiedy się starzejesz, potrzebujesz towarzystwa. "
gameforge.locale.monster_chat_20001_5 = "Moje najlepsze dni już minęły. "
gameforge.locale.monster_chat_20002_1 = "Słońce jest takie gorące... "
gameforge.locale.monster_chat_20002_2 = "Doskonała pogoda! Muszę zrobić pranie. "
gameforge.locale.monster_chat_20002_3 = "Gdzie jest ten mój leniwy mąż?! "
gameforge.locale.monster_chat_20002_4 = "Nazywa siebie ojcem... a od dawna nie ma kontaktu ze swoimi dziećmi! "
gameforge.locale.monster_chat_20002_5 = "On w ogóle nie zarabia pieniędzy. Czyta tylko książki... "
gameforge.locale.monster_chat_20003_1 = "Oooh, dudu, dziecko. Czy spałeś dobrze? "
gameforge.locale.monster_chat_20003_2 = "Pachniesz jak malutki aniołek. "
gameforge.locale.monster_chat_20003_3 = "Twoja mama jest taka szczęśliwa, że jesteś zdrowy. "
gameforge.locale.monster_chat_20003_4 = "Chcesz zwrócić na siebie uwagę? A może jesteś głodny? "
gameforge.locale.monster_chat_20003_5 = "Dobre dziecko... spij kochanie, śpij. "
gameforge.locale.monster_chat_20004_1 = "Kolejny nudny dzień... "
gameforge.locale.monster_chat_20004_2 = "Gdzie dzisiaj dostanę jakieś jedzenie? "
gameforge.locale.monster_chat_20004_3 = "Przeciwnicy już mnie nudzą... "
gameforge.locale.monster_chat_20004_4 = "Zobaczymy. Może powinienem wpaść do lokalu Octavios? "
gameforge.locale.monster_chat_20004_5 = "Oh, przy okazji, wygląda na to, że nie będzie już wojen. "
gameforge.locale.monster_chat_20005_1 = "Dusza Mistrza Garncarstwa przebiega przez koniuszki jego palców. "
gameforge.locale.monster_chat_20005_2 = "Od kiedy kraina pogrążyła się w chaosie, wszędzie panoszy się zło. "
gameforge.locale.monster_chat_20005_3 = "Każdy chce walczyć. "
gameforge.locale.monster_chat_20005_4 = "Modlę się o spokojny i wolny od wojen kraj. "
gameforge.locale.monster_chat_20005_5 = "Hej, myślę że powinieneś sobie troszkę odpocząć. "
gameforge.locale.monster_chat_20006_1 = "Oh, co powinienem teraz zrobić? "
gameforge.locale.monster_chat_20006_2 = "Wciąż szukam mojego brata. A obiecywał, że zawsze przy mnie będzie! "
gameforge.locale.monster_chat_20006_3 = "Teraz widzę tylko wojowników z mieczami. "
gameforge.locale.monster_chat_20006_4 = "Tęsknię za bratem. "
gameforge.locale.monster_chat_20006_5 = "Czy ktoś widział mojego brata? "
gameforge.locale.monster_chat_20007_1 = "Hmm, to naprawdę świeże mięsko! "
gameforge.locale.monster_chat_20007_2 = "To dobrze, że ludzie zaczęli jeść więcej mięsa. "
gameforge.locale.monster_chat_20007_3 = "Biznes idzie dobrze, ale dalej trudno jest zdobyć mięso. "
gameforge.locale.monster_chat_20007_4 = "Co się dzieje z mięsem, które zamówiliśmy? "
gameforge.locale.monster_chat_20007_5 = "Jeśli nie dotrze na czas, będziemy musieli iść na polowanie. "
gameforge.locale.monster_chat_20008_1 = "Mam nadzieję, że dzisiaj będę miał dużo klientów. Jest piękna pogoda. "
gameforge.locale.monster_chat_20008_2 = "Ten leniwy pies na pewno niedługo przyjdzie i będzie chciał coś zjeść. "
gameforge.locale.monster_chat_20008_3 = "Oh, co za biedaczysko! "
gameforge.locale.monster_chat_20008_4 = "Kiedyś był naprawdę wielkim wojownikiem. "
gameforge.locale.monster_chat_20008_5 = "Zastanawiam się, czemu już nie walczy. "
gameforge.locale.monster_chat_20009_1 = "Ech, ci młodzi faceci... tacy żywi i pełni zapału! "
gameforge.locale.monster_chat_20009_2 = "Wojownik wygląda o wiele lepiej kiedy niesie swą broń. "
gameforge.locale.monster_chat_20009_3 = "Teraz wszyscy mają taki zbrodniczy umysł. "
gameforge.locale.monster_chat_20009_4 = "Poskrom swe serce, skoncentruj umysł i ukołysz swą duszę. "
gameforge.locale.monster_chat_20009_5 = "Nie ma niezawodnej recepty na szczęście. Jest jednak parę prostych zasad, których należy stale przestrzegać. "
gameforge.locale.monster_chat_20010_1 = "Przynoszę Ci wspaniały przedmiot. "
gameforge.locale.monster_chat_20010_2 = "Towary z dalekich krajów! Piękne, egzotyczne przedmioty! "
gameforge.locale.monster_chat_20010_3 = "Podejdźcie! Chodźcie tutaj wszyscy! "
gameforge.locale.monster_chat_20010_4 = "Chodźcie ludzie, kupujcie! Taka okazja się nie powtórzy! "
gameforge.locale.monster_chat_20010_5 = "Wokół mnie jest za dużo złodziei. Powinni trzymać się z dala od mojego majątku! "
gameforge.locale.monster_chat_20011_1 = "Wszystko tutaj wyschło... "
gameforge.locale.monster_chat_20011_2 = "Jakość zboża jest gorsza niż kiedyś. "
gameforge.locale.monster_chat_20011_3 = "Co powoduje, że zboże rośnie wolniej? "
gameforge.locale.monster_chat_20011_4 = "To zboże jest kruche i smakuje zgnilizną. "
gameforge.locale.monster_chat_20011_5 = "To zły znak. W naszej wiosce rolnictwo to podstawa. "
gameforge.locale.monster_chat_20012_1 = "Ciastka Ryżowe! Smaczne Ciastka Ryżowe! "
gameforge.locale.monster_chat_20012_2 = "Sprzedaję słodkie Ciastka Ryżowe. "
gameforge.locale.monster_chat_20012_3 = "Zgadza się, interes nie idzie za dobrze... "
gameforge.locale.monster_chat_20012_4 = "A może spróbuję w innym mieście? "
gameforge.locale.monster_chat_20012_5 = "Ale dookoła mnie jest tyle dzikich drapieżników... "
gameforge.locale.monster_chat_20013_1 = "W takie dni, przebywanie na łodzi to najlepszy pomysł. "
gameforge.locale.monster_chat_20013_2 = "Świetna pogoda na wędkowanie! "
gameforge.locale.monster_chat_20013_3 = "Jeszcze tylko węzeł... gotowe! Żadna ryba nie odgryzie tego haka! "
gameforge.locale.monster_chat_20013_4 = "Ten przeklęty niedźwiedź zniszczył moją Wędkę! "
gameforge.locale.monster_chat_20013_5 = "Przydałoby się kiedyś znowu połatać te sieci. "
gameforge.locale.monster_chat_20014_1 = "Huh! "
gameforge.locale.monster_chat_20014_2 = "Nie martw się, taki już jestem. "
gameforge.locale.monster_chat_20014_3 = "Haaaaa! "
gameforge.locale.monster_chat_20014_4 = "I co ja mam uczynić, aby... O nie, co ja akurat powiedziałem? "
gameforge.locale.monster_chat_20014_5 = "Nie jestem odważny... "
gameforge.locale.monster_chat_20015_1 = "Ten topór służy mi od ponad 20 lat. "
gameforge.locale.monster_chat_20015_2 = "Ciągle ostry, ale może już nie tak jak kiedyś... "
gameforge.locale.monster_chat_20015_3 = "Chcę się wzmocnić poprzez używanie go. "
gameforge.locale.monster_chat_20015_4 = "Ale potrzebuję skamielin z minerałami. "
gameforge.locale.monster_chat_20015_5 = "Gdzie mogę je znaleźć? "
gameforge.locale.monster_chat_20016_1 = "Klientela przychodzi tłumnie. "
gameforge.locale.monster_chat_20016_2 = "Mam nadzieję, że zdążę ze wszystkim na czas. "
gameforge.locale.monster_chat_20016_3 = "Stal o stal, mocniej wal... "
gameforge.locale.monster_chat_20016_4 = "Tak powinien brzmieć młot! "
gameforge.locale.monster_chat_20016_5 = "Gorąco tutaj. "
gameforge.locale.monster_chat_20017_1 = "Nie mam ochoty grać na instrumencie. "
gameforge.locale.monster_chat_20017_2 = "Mój instrument nie jest w dobrym stanie. "
gameforge.locale.monster_chat_20017_3 = "Może powinienem sobie załatwić nowy instrument? "
gameforge.locale.monster_chat_20017_4 = "Mój instrument jest stary i wysłużony... kocham stare rzeczy! "
gameforge.locale.monster_chat_20017_5 = "Muszę niezwłocznie znaleźć nowe struny, aby wymienić te zerwane. "
gameforge.locale.monster_chat_20018_1 = "Jestem taki zajęty! "
gameforge.locale.monster_chat_20018_2 = "Każdego dnia, przychodzą do mnie ranni ludzie. "
gameforge.locale.monster_chat_20018_3 = "Nawet nie mam czasu żeby gdzieś wyjść! "
gameforge.locale.monster_chat_20018_4 = "Skończyły się nam lekarstwa. "
gameforge.locale.monster_chat_20018_5 = "Chciałbym, aby ktoś dostarczył mi składniki na lekarstwo... "
gameforge.locale.monster_chat_20019_1 = "Od 30 lat chodzę w Góry z moim łukiem! "
gameforge.locale.monster_chat_20019_2 = "Dzięki temu mogłem wspomóc żonę i wychować trójkę dzieci. "
gameforge.locale.monster_chat_20019_3 = "Życie jest ciężkie, ale i tak warto je żyć. "
gameforge.locale.monster_chat_20019_4 = "A teraz pękła cięciwa, nie mogę polować... jak mam wyżywić rodzinę?! "
gameforge.locale.monster_chat_20019_5 = "Jeżeli nie naprawię łuku, będę musiał odejść na emeryturę... "
gameforge.locale.monster_chat_20020_1 = "Hey! Na co się tak gapisz?! "
gameforge.locale.monster_chat_20020_2 = "Odejdź stąd! "
gameforge.locale.monster_chat_20020_3 = "Oh, jak długo jeszcze będę się musiał ukrywać? "
gameforge.locale.monster_chat_20020_4 = "Czy umrę jako samotnik, całkiem zapomniany? "
gameforge.locale.monster_chat_20020_5 = "Aaaaah, mam nadzieję, że ktoś mi pomoże... "
gameforge.locale.monster_chat_20021_1 = "Ten dzień skończy się tak jak wszystkie inne. "
gameforge.locale.monster_chat_20021_2 = "Co teraz mogę zrobić, huh? "
gameforge.locale.monster_chat_20021_3 = "Nigdy nie myślałem, że to tak się skończy. "
gameforge.locale.monster_chat_20021_4 = "Nie! On nie może być jedynym, który tak skończy! "
gameforge.locale.monster_chat_20021_5 = "Mam nadzieję, że ktoś go pomści! "
gameforge.locale.monster_chat_20022_1 = "Znowu piękny dzień! "
gameforge.locale.monster_chat_20022_2 = "Zaginął mąż Ariyoung. "
gameforge.locale.monster_chat_20022_3 = "Chciałem pomóc, ale ten upór... "
gameforge.locale.monster_chat_20022_4 = "Jeśli by mnie poślubiła, miałaby znacznie lepsze życie. "
gameforge.locale.monster_chat_20022_5 = "Może powinienem okazywać swoje uczucia lepiej... "
gameforge.locale.monster_chat_20023_1 = "Oh nie! Co ja zrobię kiedy moja żona się o tym dowie?! "
gameforge.locale.monster_chat_20023_2 = "Gdybym tylko mógł żyć z czytania książek... "
gameforge.locale.monster_chat_20023_3 = "Książki są takie tajemnicze. "
gameforge.locale.monster_chat_20023_4 = "Nie mogę tak żyć cały czas, ale... "
gameforge.locale.monster_chat_20023_5 = "Myślę, że mogę coś zrobić dla mojej żony, ale... "
gameforge.locale.monster_chat_20024_1 = "No dawaj! Włóż więcej uczucia w swoje ruchy! "
gameforge.locale.monster_chat_20024_2 = "Co ty tam robisz?! Nie możesz się pospieszyć? "
gameforge.locale.monster_chat_20024_3 = "Wyobraź sobie po prostu, jak wojownik włada mieczem i powtórz ten ruch! "
gameforge.locale.monster_chat_20024_4 = "Gdzie jest ten gamoń z mieczem? "
gameforge.locale.monster_chat_20024_5 = "To pies! Nie jesteś mężczyzną, ha! "
gameforge.locale.monster_chat_2091_1 = "ciach, ciach... włosy, szyje... "
gameforge.locale.monster_chat_2091_2 = "Mam coś dla ciebie: wieczny sen! "
gameforge.locale.monster_chat_2091_3 = "Głęboki wdech i.... wydech. Nie martw się, to tylko trucizna! "
