gameforge.locale.monster_chat_2107_3 = "To się spodoba dowódcy... "
gameforge.locale.monster_chat_2107_4 = " "
gameforge.locale.monster_chat_2107_5 = "Ruszajmy! "
gameforge.locale.monster_chat_2107_6 = "Nadciąga strzała! "
