gameforge.locale.old_pirate.do_you_mean_this_medicine = "Oto lekarstwo! "
gameforge.locale.old_pirate.etc_shop_start_say = "Handlarka Różności:[ENTER]Stary Pirat? Oczywiście, że go znam.[ENTER]To ten gość, który zawsze przeklina i się drapie,[ENTER]prawda? Zawsze przychodzi kupić jakieś płynne[ENTER]lekarstwo. Wydał wiele tysięcy Yang na Czerwone[ENTER]Mikstury. "
gameforge.locale.old_pirate.etc_shop_start_say2 = "Handlarka Różności:[ENTER]Skąd na miłość boską ma on tyle Yang, skoro nawet[ENTER]nigdzie nie pracuje? Już mu tyle razy mówiłam,[ENTER]żeby poszedł na badania do lekarza, ale on mnie w[ENTER]ogóle nie słucha. Czerwone lekarstwo łagodzi[ENTER]jedynie symptomy, nie leczy jednak samej choroby.[ENTER]Boję się, że źle się to skończy. "
gameforge.locale.old_pirate.etc_shop_talk_about_old_pirate = "Znasz Starego Pirata? "
gameforge.locale.old_pirate.order_accept = "Oczywiście, pomogę Ci. "
gameforge.locale.old_pirate.order_accept_answer = "Stary Pirat:[ENTER]Świetnie. Powodzenia![ENTER]Lekarz na pewno jest w mieście. "
