gameforge.locale.old_pirate.order_refuse_answer = "Stary Pirat:[ENTER]W takim razie, nie mam nic więcej do powiedzenia! "
gameforge.locale.old_pirate.order_say1 = "Stary Pirat:[ENTER]Cholera! Czego chcesz?[ENTER]Cholera! Ten rupieć sprzedawca sklepowy[ENTER]powiedział Ci coś o mnie, tak?[ENTER]Taa, to prawda.[ENTER]Ale ja nie mam czasu, żeby iść do lekarza.[ENTER]Mam tylu wrogów! "
gameforge.locale.old_pirate.order_say2 = "Stary Pirat:[ENTER]Nigdy nie wiesz, czy jakiś drań nagle nie[ENTER]znajdzie się przy tobie i nie dźgnie Cię! Ale to[ENTER]naprawdę swędzi... Nie mogę wytrzymać... Więc -[ENTER]czy mógłbyś pójść do lekarza i przynieść mi[ENTER]lekarstwo na tą chorobę?[ENTER]Zostaniesz sowicie wynagrodzony... "
gameforge.locale.old_pirate.order_too_late = "Stary Pirat:[ENTER]Ktoś inny przyniósł mi już lekarstwo.[ENTER]Więc nie dostaniesz ode mnie nagrody! "
