gameforge.main_quest_lv72._40_say = "Teraz jednak dowiedziałem się, że Smocze Kapłanki[ENTER]posiadają zdolności przywołania świętych sił,[ENTER]które są w stanie przepędzić złe duchy. "
gameforge.main_quest_lv72._50_say = "W Czerwonym Lesie znajdowała się niegdyś[ENTER]poświęcona świątynia Smoczych Kapłanek. Poszukaj[ENTER]tam ruin i dowodów świadczonych o istnieniu ich[ENTER]kultury. Zwiadowcy donieśli mi o Zwietrzałej[ENTER]Płycie, którą teraz porasta trawa... Może[ENTER]znajdziemy na niej dalsze wskazówki. "
gameforge.main_quest_lv72._60_sendLetter = "Odkrycie "
