gameforge.main_quest_lv78._40_say = "Czerwony Kwarc Piekieł jest produkowany z trzech[ENTER]substancji: Ziemi Wulkanicznej, Płonącej Grzywy[ENTER]oraz Kamienia Węgielnego. Wszystkie substancje[ENTER]znajdziesz w Ognistej Ziemi. "
gameforge.main_quest_lv78._50_say = "Dokonałeś tego! Nareszcie posiadamy potrzebne %d[ENTER]szt. Czerwonego Kwarcu Piekieł. Za każdym razem[ENTER]od nowa podziwiam twoją odwagę i ducha walki! "
gameforge.main_quest_lv78._60_say = "Brakuje jeszcze jednej substancji. Potrzebujemy[ENTER]Ziemi Wulkanicznej, Płonącej Grzywy oraz Kamienia[ENTER]Węgielnego. "
gameforge.main_quest_lv78._70_say = "Przykro mi, ale w jednym dniu mogę wyprodukować[ENTER]tylko siedem sztuk Czerwonego Kwarcu Piekieł. Ta[ENTER]produkcja to wyzwanie dla moich magicznych[ENTER]zdolności. W końcu nie jestem magiem! "
gameforge.main_quest_lv78._80_say = "Do dzieła! Wymieszam teraz te trzy substancje ze[ENTER]sobą i spróbuję wyprodukować Czerwony Kwarc[ENTER]Piekieł. "
