gameforge.main_quest_lv93._020_say = "Admirał Angmur chce z tobą porozmawiać. "
gameforge.main_quest_lv93._030_say = "Depczemy już po piętach Wasalom Mroku. "
gameforge.main_quest_lv93._040_say = "Aby ich pokonać, musimy wzmocnić twoje siły.[ENTER]Wiesz co to jest Wewnętrzne Światło? To[ENTER]skoncentrowana energia. Mają ją w sobie niektóre[ENTER]mistyczne stworzenia lub rośliny. Yon-I-Walker[ENTER]może z niej przyrządzić środek wzmacniający. Idź[ENTER]do niego. "
gameforge.main_quest_lv93._050_say = "Zgoda. "
gameforge.main_quest_lv93._060_say = "Ach, to jest mój wybawiciel![ENTER]Wewnętrzne Światło, och tak... Dzięki mojemu[ENTER]środkowi wzmacniającemu dorównasz siłą Wasalom Mroku. "
gameforge.main_quest_lv93._070_say = "Potrzebuję do tego Wewnętrzne Światło Cyklopów[ENTER]oraz Gigantów. Ale pamiętaj, noszą je w sobie[ENTER]tylko sami przywódcy tych plemion. Zajmij się[ENTER]najpierw Cyklopami. "
gameforge.main_quest_lv93._080_say = "Zgoda. "
gameforge.main_quest_lv93._090_sayTitle = "Wewnętrzne Światło I "
gameforge.main_quest_lv93._100_say = "Pokonaj Polifema, Króla Cyklopów i odbierz mu jego[ENTER]Wewnętrzne Światło. "
gameforge.main_quest_lv93._110_say = "Przychodzisz z pustymi rękami? W ten sposób nie[ENTER]będę w stanie ci pomóc. Przynieś mi Wewnętrzne[ENTER]Światło! "
gameforge.main_quest_lv93._120_1_notice = "Szuka cię Yon-I-Walker. "
gameforge.main_quest_lv93._130_say = "Porozmawiaj z Yon-I-Walker. "
gameforge.main_quest_lv93._140_say = "A więc to jest Wewnętrzne Światło... Zanim będę[ENTER]mógł poddać przeróbce, muszę go oczyścić. To z[ENTER]pewnością potrwa jakiś czas. Dam ci znać, gdy będę[ENTER]gotów. "
gameforge.main_quest_lv93._150_sendLetter = "Wzmocnij siły II "
gameforge.main_quest_lv93._160_say = "Szuka cię Yon-I-Walker. "
gameforge.main_quest_lv93._170_say = "Wewnętrzne Światło Polifema jest oczyszczone.[ENTER]Potrzebujemy też światła Gigantów.[ENTER]Aby je zdobyć, musisz pokonać Króla Wobba. "
gameforge.main_quest_lv93._180_say = "Natychmiast wyruszam. "
gameforge.main_quest_lv93._190_sayTitle = "Wewnętrzne Światło II "
gameforge.main_quest_lv93._200_say = "Pokonaj Król Gigantów Wobba i odbierz mu jego[ENTER]Wewnętrzne Światło. "
gameforge.main_quest_lv93._210_say = "Przychodzisz z pustymi rękami? Zdziwiłbym się[ENTER]okropnie, jeśli nie podołałbyś temu zadaniu.[ENTER]Spróbuj ponownie! "
gameforge.main_quest_lv93._220_1_notice = "Trzymasz w swoich rękach Wewnętrzne Światło Gigantów. "
gameforge.main_quest_lv93._220_2_notice = "Zanieś je do Yon-I-Walker. "
gameforge.main_quest_lv93._230_say = "Porozmawiaj z Yon-I-Walker. "
gameforge.main_quest_lv93._240_say = "Wewnętrzne Światło Króla Wobba posiada olbrzymią[ENTER]moc. Wydaje mi się, że to z powodu tej masy. Teraz[ENTER]muszę go tylko oczyścić. A to może potrwać chwilkę. "
gameforge.main_quest_lv93._250_sendLetter = "Wzmocnij siły III "
gameforge.main_quest_lv93._260_say = "Szuka cię Yon-I-Walker. "
gameforge.main_quest_lv93._270_say = "Bardzo dobrze. Potrzebuję twojej pomocy. Aby[ENTER]całkowicie zadziałał mój Eliksir Wzmocnienia[ENTER]potrzebuje jeszcze jednego składnika: Esencji[ENTER]Milenium. "
gameforge.main_quest_lv93._280_say = "Esencja Milenium? Nie słyszałem nigdy o niej. "
gameforge.main_quest_lv93._290_say = "To silna mikstura sporządzona z różnych esencji[ENTER]ziołowych. Znasz Octavio? Mieszka na kontynencie[ENTER]i jest jedynym, który jest w stanie przyrządzić[ENTER]Esencję Milenium. Idź do niego. Mam nadzieję, że[ENTER]nam pomoże. Czasem jest uparty... "
gameforge.main_quest_lv93._300_say = "Zgoda. "
gameforge.main_quest_lv93._310_sayTitle = "Porozmawiaj z Octavio! "
gameforge.main_quest_lv93._320_say = "Odszukaj Octavio. Mieszka on na kontynencie i jest[ENTER]jedynym, który może przyrządzić Esencję Milenium. "
gameforge.main_quest_lv93._330_say = "Hej! Co mogę ci przynieść? Może zupkę samgyetang?[ENTER]Specjalność domu z... "
gameforge.main_quest_lv93._340_say = "Octavio, nie jestem tutaj z powodu twojej[ENTER]wyborowej kuchni! Potrzebuję coś innego: Esencję[ENTER]Milenium. "
gameforge.main_quest_lv93._350_say = "Oj, psss, nie tak głośno! Esencja Milenium... Nie,[ENTER]to nie wchodzi w rachubę. Od kogo się o niej[ENTER]dowiedziałeś? Tylko nieliczni radzą sobie z jej[ENTER]gigantyczną mocą, którą może ich obdarzyć. To jest[ENTER]zbyt ryzykowne. A teraz sio, zanim jeszcze ktoś to[ENTER]usłyszy... "
gameforge.main_quest_lv93._360_say = "Octavio, zaczekaj i wysłuchaj mnie! Przysyła mnie[ENTER]Yon-I-Walker ze Skały Mrocznego Smoka. Koniecznie[ENTER]potrzebujemy tej esencji. Bez niej nie damy rady[ENTER]pokonać Wasali Mroku. I świat, który teraz znamy,[ENTER]przestanie istnieć. Tylko ty możesz ją sporządzić.[ENTER]Proszę! "
gameforge.main_quest_lv93._370_say = "Powiadasz Yon-I-Walker? Hm, to mój stary dobry[ENTER]przyjaciel. Nie jedną butelkę wyborowego wina[ENTER]wypiliśmy razem podczas naszych nocnych rozmów.[ENTER]Muszę się zastanowić. Przyjdź do mnie jutro, to[ENTER]powiem ci, czy się zgadzam. "
gameforge.main_quest_lv93._380_say = "Dobra, wpadnę jutro znowu. "
gameforge.main_quest_lv93._390_say = "Jeszcze nie zdecydowałem. Przyjdź później. "
gameforge.main_quest_lv93._400_npcChat = "Esencja Milenium "
gameforge.main_quest_lv93._410_say = "Octavio chciałby z tobą porozmawiać. "
gameforge.main_quest_lv93._420_say = "Rozmyślałem przez całą noc nad twoją prośbę. I[ENTER]postanowiłem, że zaryzykuję. Wiem o tym, że to[ENTER]jest prawidłowa decyzja. Tak, pomogę ci. "
gameforge.main_quest_lv93._430_say = "Wspaniale! "
gameforge.main_quest_lv93._440_say = "Jednak nie mam jest w zapasie. Czy przyniesiesz mi[ENTER]kilka składników? To przyrządzę ją dla ciebie. "
gameforge.main_quest_lv93._450_say = "Oczywiście. Co potrzebujesz? "
gameforge.main_quest_lv93._460_say = "Potrzebuję Korzeni Gango, Grzybów Tue oraz Róż[ENTER]Alpejskich.  Z każdego składnika po 200 sztuk. "
gameforge.main_quest_lv93._470_say = "Natychmiast wyruszam. Gdzie mogę znaleźć te[ENTER]składniki? "
gameforge.main_quest_lv93._480_say = "Rośliny te znajdziesz w różnych miejscach na[ENTER]kontynencie.[ENTER]Podczas gdy ty będziesz szukał składników, ja[ENTER]rozpocznę przygotowania. "
gameforge.main_quest_lv93._490_sayTitle = "Receptura Milenium "
gameforge.main_quest_lv93._500_say = "Poszukaj Korzenia Gango, Grzyba Tue oraz Róży[ENTER]Alpejskiej.[ENTER]Potrzebujesz po 200 sztuk. "
gameforge.main_quest_lv93._510 = "Rośliny te znajdziesz w różnych miejscach na[ENTER]kontynencie.[ENTER]Podczas gdy ty będziesz szukał składników, ja[ENTER]rozpocznę przygotowania. "
gameforge.main_quest_lv93._520_say = "Ach, wspaniale, jesteś z powrotem. I masz wszystko[ENTER]co potrzeba. No to zabieram się do roboty. Teraz[ENTER]potrzebuję spokoju. Przyjdź jutro o tej samej[ENTER]porze. Mam nadzieję, że do tego czasu esencja[ENTER]będzie gotowa. "
gameforge.main_quest_lv93._530_say = "W porządku. Do jutra. "
gameforge.main_quest_lv93._540_say = "Rozumiem, że ci śpieszno, ale musisz jeszcze[ENTER]trochę poczekać. Przyjdź później. "
gameforge.main_quest_lv93._550_say = "Szuka cię Octavio. "
gameforge.main_quest_lv93._560_say = "No proszę, przychodzisz w samą porę. Właśnie[ENTER]zakończyłem przyrządzanie Esencji Milenium! Możesz[ENTER]ją teraz zanieś do Yon-I-Walker. "
gameforge.main_quest_lv93._570_say = "Dziękuję ci. "
gameforge.main_quest_lv93._580_sendLetter = "Esencja Milenium "
gameforge.main_quest_lv93._590_say = "Szuka cię Yon-I-Walker. "
gameforge.main_quest_lv93._600_say = "Masz Esencję Milenium! Teraz mam wszystkie[ENTER]składniki potrzebne do mojego Eliksiru[ENTER]Wzmocnienia. Zawołam cię, jak tylko go przyrządzę. "
gameforge.main_quest_lv93._610_say = "Dobrze. "
gameforge.main_quest_lv93._620_say = "Możesz kontynuować przygodę, gdy tylko osiągniesz[ENTER]poziom 94. "
gameforge.main_quest_lv93._630_say = "%s: %d szt., %s: %d szt., %s: %d szt. "
gameforge.main_quest_lv93._640_say = "Ja mam co do roboty, muszę zajmować się moją[ENTER]gospodą i nie mogę marnować czasu. Przynieś mi[ENTER]brakujące składniki - i pośpiesz się! "
gameforge.main_quest_lv93._650_1_notice = "Udało ci się znaleźć wszystkie składniki potrzebne[ENTER]do przyrządzenia Esencji Milenium. "
gameforge.main_quest_lv93._650_2_notice = "Wróć do Octavio. "
