gameforge.new_quest_lv22._098_say = "Jak to, znowu zgubiłeś lekarstwo? W życiu nie[ENTER]spotkałem większej fajtłapy! Ale cóż, mówi się[ENTER]trudno i żyje się dalej. "
gameforge.new_quest_lv22._100_say_reward = "Otrzymujesz 25.000 Punktów Doświadczenia. "
