gameforge.new_quest_lv29._030_target = "Taurean "
gameforge.new_quest_lv29._031_say = "Drogocenne pamiątki rodzinne Yonaha, ktoś rozbił[ENTER]wspaniałe trzy Wazy z Seledynowej Porcelany.[ENTER]Yonah jest przekonany, że to sprawka Taureana.[ENTER]Udaj się do Taureana i dowiedz się, czy to[ENTER]prawda. "
gameforge.new_quest_lv29._032_say = "Taurean znajduje się w %s. "
gameforge.new_quest_lv29._033_say_title = "Taurean: "
gameforge.new_quest_lv29._034_say_reward = "Dlaczego spoglądasz na mnie tak ponuro? Nic[ENTER]przecież złego nie zrobiłem... "
gameforge.new_quest_lv29._035_say_reward = "Co zrobiłeś z Wazami Yonaha? "
gameforge.new_quest_lv29._036_say_reward = "Wazami? Yonah? Naprawdę nie wiem o czym mówisz... "
gameforge.new_quest_lv29._037_say_reward = "To wytłumacz mi, dlaczego trzy są rozbite. "
gameforge.new_quest_lv29._038_say_reward = "Przysięgam, dzisiaj nie widziałem na oczy ani[ENTER]Wazy ani Yonah. Cały dzień walczyłem z Harang.[ENTER]Nawet zamknęła mnie na kilka godzin w stogu[ENTER]siana! Idź do niej i zapytaj się! Nie kłamię! "
gameforge.new_quest_lv29._040_target = "Harang "
gameforge.new_quest_lv29._041_say = "Taurean twierdzi, że jest niewinny. Powiedział,[ENTER]że bawił się przez cały dzień z Harang, dlatego[ENTER]też nie miał sposobności rozbić tych Waz. Idź do[ENTER]Harang i zapytaj się czy to prawda. "
gameforge.new_quest_lv29._042_say_reward = "Harang, widziałaś dzisiaj Taureana? "
gameforge.new_quest_lv29._043_say_title = "Harang: "
gameforge.new_quest_lv29._044_say_reward = "Taurean? Pewnie, bawiliśmy się przez cały dzień.[ENTER]Coś nie tak? "
gameforge.new_quest_lv29._045_say_reward = "Yonah twierdzi, że Taurean zbił trzy Wazy. "
gameforge.new_quest_lv29._046_say_reward = "Yonah jest podły. Natychmiast przepędza mnie i[ENTER]Taurean jak tylko podejdziemy bliżej. Ciągle boi[ENTER]się o swoją porcelanę. Jego drogocenne Wazy może[ENTER]dotknąć tylko jego przyjaciel, który jest[ENTER]stajennym. Może on je rozbił?! "
gameforge.new_quest_lv29._047_say = "Harang znajduje się w %s. "
gameforge.new_quest_lv29._050_target = "Stajenny "
gameforge.new_quest_lv29._051_say = "Harang potwierdziła słowa Taureana. Jest[ENTER]niewinny, ale może są oni w zmowie. Zanim jednak[ENTER]oskarżysz kogoś, zapytaj się jeszcze przyjaciela[ENTER]Yonaha, który jest stajennym. "
gameforge.new_quest_lv29._052_say = "Ach, ten poczciwy Yonah. Marti się o swoją[ENTER]porcelanę i podejrzewa dzieciaki z wioski. Winne[ENTER]są jednak Kamienie Metin, odkąd zatruły nasz kraj[ENTER]nie są nasze surowce takie jak dawniej. Nawet[ENTER]porcelana stała się bardziej krucha. Jednak Yonah[ENTER]nie chce o tym słyszeć... "
