gameforge.subquest_16._10_targetVid = "Poszukiwanie Uzbrojonej Kawalerii "
gameforge.subquest_16._110_targetVid = "Idź do Yu-Hwan "
gameforge.subquest_16._120_say = "Życie jest takie piękne... ach, miłość, cudowna[ENTER]miłość... ![ENTER]Znowu ty. Czego tym razem chcesz?[ENTER]Informacji o Uzbrojonej Kawalerii?[ENTER]Dlaczego o to pytasz?[ENTER]Kapitan ma zapewne ambitne plany? "
gameforge.subquest_16._130_sayTitle = "Yu-Hwan: "
gameforge.subquest_16._140_say = "Zachowały się jedynie nieliczne oryginalne teksty[ENTER]z tamtych czasów, ale znam kilka pieśni o[ENTER]Uzbrojonej Kawalerii. Chwila, nastroję tylko swój[ENTER]instrument...[ENTER]Ach, śpieszysz się? W dzisiejszych czasach ludzie[ENTER]nie mają cierpliwości do słuchania...[ENTER]No Dobrze, streszczę się. Kawaleria Isupsa była[ENTER]niemalże nie do pokonania. Doskonale wyszkoleni[ENTER]żołnierze, ciężkie metalowe zbroje a przy tym[ENTER]szybka i zwinna. "
gameforge.subquest_16._150_say = "To była najlepsza armia, jaka kiedykolwiek[ENTER]stąpała po tym kontynencie. Wiele krajów musiało[ENTER]polec, jako że nic nie było w stanie zatrzymać[ENTER]tej kawalerii. Dzisiaj nie byłoby inaczej. Do[ENTER]stworzenia takiej armii nieodzowny jest jednak[ENTER]ciężki trening oraz dużo metalu najwyższej[ENTER]jakości. Powinieneś zapytać Domokrążcy oraz[ENTER]uczonego Uriela. Na pewno znają więcej[ENTER]szczegółów. "
gameforge.subquest_16._160_makequestbutton = "Do Domokrążcy "
gameforge.subquest_16._170_qSetTitle = "Uzbrojona Kawaleria "
gameforge.subquest_16._180_targetVid = "Domokrążca "
gameforge.subquest_16._190_sayReward = "Muzyk Yu-Hwan twierdzi, że Domokrążca i[ENTER]Mędrzec Uriel mogą mieć informacje o Uzbrojonej[ENTER]Kawalerii. Idź najpierw do Domokrążcy. "
gameforge.subquest_16._200_sayTitle = "Domokrążca: "
gameforge.subquest_16._20_npcChat = "Uzbrojona Kawaleria "
gameforge.subquest_16._210_say = "Czego chcesz od prostego Domokrążcy jak ja?[ENTER]Żelazne Zbroje Uzbrojonej Kawalerii? To przecież[ENTER]stare bajki. Kogo to dzisiaj jeszcze interesuje?[ENTER]Ja muszę zarabiać na życie i nie mam czasu na[ENTER]całodniowe pogawędki! "
gameforge.subquest_16._220_say = "Coraz ciężej wiązać koniec z końcem. Od kiedy te[ENTER]kamienie spadają z nieba, zacząłem... co takiego?[ENTER]A właśnie, Kawaleria.[ENTER]O ile mi wiadomo, nosili specjalne zbroje,[ENTER]których produkcja wymagała niezwykłych[ENTER]umiejętności. Są to najlepsze zbroje wszech[ENTER]czasów! Zwykłe noże, ba, nawet strzały po prostu[ENTER]się odbijają! "
gameforge.subquest_16._230_say = "To, że pancerze Uzbrojonej Kawalerii były czymś[ENTER]wyjątkowym, widać choćby po ilości pieniędzy[ENTER]potrzebnych do stworzenia Kawalerii. Mówi się, że[ENTER]pochłonęła skarbce całych królestw! Gdybym[ENTER]sprzedawał takie zbroje, nie musiałbym się już[ENTER]nigdy martwić o pieniądze! "
gameforge.subquest_16._240_say = "Dzisiaj można nawet wykuwać zbroje z Kamieni[ENTER]Metin. Stworzenie drugiej Uzbrojonej Kawalerii[ENTER]wydaję się dzięki tej nowej technice zbrojeniowej[ENTER]wcale nie takie niemożliwe! Ale za dużo gadam.[ENTER]Muszę już iść. Na razie! "
gameforge.subquest_16._250_sayTitle = "Uzbrojona Kawaleria "
gameforge.subquest_16._260_say = "Domokrążca wiedział coś na temat metalowych zbroi[ENTER]Uzbrojonej Kawalerii. Idź teraz do mędrca Uriela[ENTER]w sąsiedniej wiosce i spytaj się go o trening. "
gameforge.subquest_16._270_sayTitle = "Uriel: "
gameforge.subquest_16._280_say = "Co tym razem jest powodem twojego przybycia?[ENTER]Uzbrojona Kawaleria? Oczywiście, że wiem coś[ENTER]o niej, każdy wie. Po wsze czasy armia ta[ENTER]będzie zapamiętana jako najlepsza w historii![ENTER]Wiele z tego, co ludzie sobie opowiadają, jest[ENTER]zapewne przesadą, jeźdźcy ci wzbudzali jednak[ENTER]niesamowitą grozę. I to nie bez powodu. "
gameforge.subquest_16._290_say = "Uzbrojona Kawaleria górowała nad każdą inna[ENTER]armią. Była to przede wszystkim zasługa nieludzko[ENTER]ciężkiego treningu. Tylko najlepsi byli w stanie[ENTER]ukończyć szkolenie, niejeden zginął próbując.[ENTER]Podejrzewam, że gdyby zastosować te same metody[ENTER]szkolenia w naszych czasach, ani jeden żołnierz[ENTER]nie pozostałby przy życiu. "
gameforge.subquest_16._300_say = "Niektóre metody szkolenia się zachowały. Dam ci[ENTER]parę wskazówek, abyś sam mógł przekonać się o ich[ENTER]skuteczności.[ENTER]Wiem, że szaleją straszne wojny, ale nie jestem[ENTER]pewien czy ponowne stworzenie tak potężnej armii[ENTER]to dobry pomysł. "
gameforge.subquest_16._30_say = "Mam nowe zadanie dla Ciebie. Masz rozkaz zdobycia[ENTER]informacji o Uzbrojonej Kawalerii. Była ona[ENTER]najważniejszą częścią armii Isupa, Króla[ENTER]założyciela, który dawno temu zjednoczył cały[ENTER]kontynent. Uzbrojona Kawaleria była postrachem[ENTER]dla innych nacji ze względu na swoją niezwykłą[ENTER]szybkość i siłę ataku. "
gameforge.subquest_16._310_say = "Idź do Kapitana i opowiedz o wszystkim,[ENTER]czego się dowiedziałeś o Uzbrojonej Kawalerii. "
gameforge.subquest_16._320_makequestbutton = "Idź do Kapitana. "
gameforge.subquest_16._330_npcChat = "Raport o Uzbrojonej Kawalerii. "
gameforge.subquest_16._340_sayTitle = "Kapitan: "
gameforge.subquest_16._350_say = "Zdobyłeś jakieś użyteczne informacje? Opowiedz![ENTER]...[ENTER]Hm, to bardzo interesujące. Wymagane są jedynie[ENTER]silne zbroje oraz ciężki trening, nic więcej? W[ENTER]takim razie jest to do zrobienia! Taka armia[ENTER]mogłaby ponownie zjednoczyć trzy królestwa! "
gameforge.subquest_16._360_say = "Potrzebowałbym jednak bardzo dużo pieniędzy,[ENTER]wielu zdolnych kowali oraz rekrutów, którzy[ENTER]dobrowolnie przeszliby przez piekło, aby stać się[ENTER]najlepszymi z najlepszych![ENTER]Nie, nie. Nie spoglądaj na mnie w ten sposób. Nie[ENTER]martw się, póki co zrobiłeś wystarczająco. "
gameforge.subquest_16._370_say = "Proszę, weź tę nagrodę i odpocznij.[ENTER]Zobaczymy się później! "
gameforge.subquest_16._380_sayReward = "Otrzymałeś 150.000 Punktów Doświadczenia.[ENTER]Otrzymałeś 30.000 Yang.[ENTER]Otrzymałeś Złote Kolczyki. "
gameforge.subquest_16._40_say = "Dzięki zdobytym przez ciebie informacjom możemy[ENTER]wzmocnić siłę uderzeniową naszych wojsk. Odwiedź[ENTER]najpierw muzyka[ENTER]Yu-Hwan. Jestem pewien, że ma on jakieś[ENTER]informacje o Uzbrojonej Kawalerii. "
gameforge.subquest_16._50_say = "Hm, jak uważasz. Zatem do zobaczenia! "
gameforge.subquest_16._60_say = "Czy będę kiedyś dowodził taką kawalerią?[ENTER]Uważaj na siebie. "
