gameforge.training_mount._04_say = "Chcę dosiąść nowego wierzchowca! "
gameforge.training_mount._05_say = "Hej! Siedzisz na koniu? Niesłychane! Przecież[ENTER]możemy już oswajać dzikie zwierzęta! "
gameforge.training_mount._06_say = "Aby trenować dzikie zwierzęta do walki, musisz[ENTER]mieć spore doświadczenie w ich ujeżdżaniu. Poza[ENTER]tym potrzebujesz następujące przedmioty: Zwój[ENTER]Ewolucji, aby ulepszyć swojego wierzchowca do[ENTER]poziomu Dzikiego Wierzchowca, oraz Zwój Mutacji,[ENTER]aby uczynić z niego Dzielnego Wierzchowca. "
gameforge.training_mount._07_say = "Jeśli będziesz mieć jeszcze jakieś pytania, to[ENTER]możesz w każdej chwili mnie odwiedzić. Pomogę ci[ENTER]najlepiej, jak potrafię. "
gameforge.training_mount._08_say = "Chcę ulepszyć mojego wierzchowca! "
gameforge.training_mount._09_say = "A więc chcesz ulepszyć swojego wierzchowca? A[ENTER]wiesz w ogóle, jak to uczynić? No dobra, wszystko[ENTER]ci wytłumaczę. "
gameforge.training_mount._10_say = "Aby ulepszyć swojego wierzchowca do poziomu[ENTER]Dzikiego Wierzchowca, musisz zdobyć Zwój[ENTER]Ewolucji. Gdy znajdziesz Zwój Mutacji, możesz[ENTER]ulepszyć swojego pupila do poziomu Dzielnego[ENTER]Wierzchowca. Fama niesie, że zwoje te znajdują[ENTER]się w dalekiej krainie, którą zamieszkują groźne[ENTER]giganty. "
gameforge.training_mount._11_say = "Przynieś mi jeden z tych zwojów, a ja ulepszę[ENTER]twojego wierzchowca. No chyba, że już je masz? "
gameforge.training_mount._12_say = "Tak, chcę ulepszyć swojego wierzchowca! "
gameforge.training_mount._13_say = "Nie, nie teraz. "
gameforge.training_mount._14_say = "Aaa, widzę, że posiadasz już jeden zwój. A który[ENTER]dokładnie? Za pomocą Zwoju Ewolucji możesz[ENTER]zahartować swojego młodzika do boju. Za pomocą[ENTER]Zwoju Mutacji możesz go ulepszyć do poziomu[ENTER]Dzielnego Wierzchowca. "
gameforge.training_mount._15_2_say = "Posiadam Zwój Mutacji. "
gameforge.training_mount._15_say = "Posiadam Zwój Ewolucji. "
gameforge.training_mount._16_say = "Nie posiadam żadnego zwoju. "
gameforge.training_mount._17_say = "Popatrz, popatrz... Zwój Ewolucji! W takim razie[ENTER]pokaż mi jeszcze swoją Pieczęć Wierzchowca.[ENTER]Niejeden próbował wcisnąć mi jakąś marną[ENTER]podróbkę! Muszę więc sprawdzić jej autentyczność.[ENTER]Pokaż no tę pieczęć. (Przeciągnij pieczęć na[ENTER]chłopca stajennego.) "
gameforge.training_mount._18_say = "Zwój Mutacji! Znam ten przedmiot wyłącznie z[ENTER]opowiadań. Jeszcze nigdy nie miałem tego cudeńka[ENTER]w dłoniach. A teraz pokaż mi jeszcze Pieczęć[ENTER]Wierzchowca, muszę sprawdzić jej autentyczność.[ENTER](Przeciągnij pieczęć na chłopca stajennego.) "
gameforge.training_mount._19_say = "Gdy już znajdziesz pieczęć, odwiedź mnie[ENTER]ponownie, a ja wymienię ją na potężniejszą[ENTER]pieczęć. "
gameforge.training_mount._20_say = "Twoja Pieczęć Wierzchowca ma już swoje lata...[ENTER]zaczekaj, zaraz dam ci nową. No, proszę! Teraz[ENTER]możesz przywołać Dzikiego Wierzchowca. Odwiedź[ENTER]mnie ponownie, gdy znajdziesz Zwój Mutacji, a[ENTER]zmajstruję Ci jeszcze potężniejszą pieczęć. "
gameforge.training_mount._21_say = "To jest Pieczęć Wierzchowca, którą dałem ci już[ENTER]pewien czas temu. Jest już jednak nieco zużyta.[ENTER]Daj mi ją, a dostaniesz w zamian potężniejszą[ENTER]Pieczęć Wierzchowca! "
gameforge.training_mount._22_say = "Za pomocą tej pieczęci możesz przywołać swojego[ENTER]nowego wierzchowca. Jest to bardzo potężny[ENTER]przedmiot! "
