gameforge.energy_system._100_say = "Este item não possui energia suficiente. Traz-me[ENTER]um item com um nível mínimo de 35 - é a única[ENTER]forma de poder usar a minha tecnologia para[ENTER]extrair a energia de itens. "
gameforge.energy_system._110_say = "Devo destruir este item? "
gameforge.energy_system._120_select = "Sim! "
gameforge.energy_system._130_select = "Não, ainda não! "
gameforge.energy_system._140_say = "Falhei - infelizmente não consegui extrair[ENTER]qualquer Fragmento de Energia. Talvez tenha sorte[ENTER]da próxima vez. "
gameforge.energy_system._150_say = "Maravilhoso! Consegui encontrar %d Fragmento(s)[ENTER]de Energia. Aqui tens. "
gameforge.energy_system._160_npcChat = "Produzir Cristal de Energia "
gameforge.energy_system._170_say = "Já usaste todas as tuas reservas de energia? Não[ENTER]devias trabalhar tão duro e colocar a tua vida em[ENTER]risco de forma tão despreocupada. Irei precisar[ENTER]de %d Fragmentos de Energia para produzir um[ENTER]Cristal de Energia. "
gameforge.energy_system._180_say = "Não tens Fragmentos de Energia suficientes. Assim[ENTER]não consigo produzir um Cristal de Energia. Volta[ENTER]quando tiveres obtido pelo menos %d Fragmentos. "
gameforge.energy_system._190_say = "Muito bem, conseguiste obter os %d Fragmentos de[ENTER]Energia necessários. Com eles posso agora[ENTER]produzir um Cristal de Energia. Por favor espera[ENTER]um minuto. Vou tentar, mas não garanto que[ENTER]funcione. "
gameforge.energy_system._200_say = "Tenho tudo pronto para derreter os Fragmentos e[ENTER]formar um Cristal de Energia. No entanto, tenho[ENTER]de viver de alguma coisa, e as minhas ferramentas[ENTER]não caem do céu. Preciso de uma pequena taxa de[ENTER]%d Yang. Queres pagar? "
gameforge.energy_system._210_select = "Claro! "
gameforge.energy_system._220_select = "Não, agora não. "
gameforge.energy_system._230_say = "Muito bem, respeito a tua decisão.[ENTER]Volta quando tiveres mudado de ideias e queiras[ENTER]um Cristal de Energia. "
gameforge.energy_system._240_say = "Desculpa, mas tens de pagar primeiro. Não faço[ENTER]isto por caridade. O negócio é assim - dá-me o[ENTER]dinheiro e então farei o meu trabalho. "
gameforge.energy_system._250_say = "Aqui tens o teu Cristal de Energia. Mas apenas a[ENTER]primeira tentativa é sempre bem-sucedida. A[ENTER]Alquimia é uma ciência imprevisível - mesmo os[ENTER]mais pequenos erros podem estragar tudo! Existe[ENTER]sempre uma pequena hipótese de que esta[ENTER]tecnologia falhe. "
gameforge.energy_system._260_say = "Infelizmente falhei. Não consegui produzir um[ENTER]Cristal de Energia. A minha tecnologia é[ENTER]fantástica - mas o processo está repleto de[ENTER]riscos. As coisas nem sempre correm de acordo com[ENTER]o plano. Tenho a certeza que serei bem-sucedido[ENTER]da próxima vez! "
gameforge.energy_system._270_say = "Eu sabia! A minha tecnologia é fantástica! Aqui[ENTER]tens o teu Cristal de Energia. É um espécime[ENTER]brilhante! Consegues sentir o seu poder? "
