gameforge.locale.monster_chat_1003_1 = "Morte e destruição...! "
gameforge.locale.monster_chat_1003_2 = "Alguma vez viste as feridas que uma lança pode causar? "
gameforge.locale.monster_chat_1003_3 = "Sim, vai, foge! Vamos ver quão longe consegues chegar... "
gameforge.locale.monster_chat_1003_4 = "Este confronto já vale a pena pela simples visão das tuas mãos a tremer... "
gameforge.locale.monster_chat_1003_5 = "Esta escumalha ousa desafiar-me? "
gameforge.locale.monster_chat_1003_6 = "A Morte será a tua única companheira! "
gameforge.locale.monster_chat_1004_1 = "A tua alma será minha, humano! "
gameforge.locale.monster_chat_1004_2 = "Ah, carne fresca! "
gameforge.locale.monster_chat_1004_3 = "Parece que mais alguém vai encontrar aqui a sua morte! "
gameforge.locale.monster_chat_1004_4 = "Devias sofrer e soltar toda a tua angústia... "
gameforge.locale.monster_chat_1004_5 = "Achas que é essa arma ridícula que tens na mão que te irá proteger? "
gameforge.locale.monster_chat_1004_6 = "A tua coragem trouxe-te até mim. E agora ela abandonar-te-á... "
gameforge.locale.monster_chat_1035_1 = "Se eu morrer, irás sofrer a minha vingança para sempre! "
gameforge.locale.monster_chat_1035_2 = "Vou matar-te e fazer-te sofrer lentamente, muito lentamente... "
gameforge.locale.monster_chat_1035_3 = "Será um prazer fazer-te sofrer. "
gameforge.locale.monster_chat_1035_4 = "O teu corpo treme como o de uma velha mulher! "
gameforge.locale.monster_chat_1035_5 = "Consigo cheirar o teu medo, pequeno mortal. "
gameforge.locale.monster_chat_1035_6 = "Acho que está na altura de te tirar algumas partes do corpo. "
gameforge.locale.monster_chat_1036_1 = "Irei enviar-te para o teu Inferno pessoal! "
gameforge.locale.monster_chat_1036_2 = "A tua dor irá levar-te à loucura antes de morreres... "
gameforge.locale.monster_chat_1036_3 = "Geme! Suplica! Não te servirá de nada. "
gameforge.locale.monster_chat_1036_4 = "Irás morrer da pior forma que consegues imaginar... "
gameforge.locale.monster_chat_1036_5 = " "
gameforge.locale.monster_chat_1036_6 = "O teu vigor diminui. Já estás no limiar da morte. "
gameforge.locale.monster_chat_1037_1 = "Estás preparado para caminhar na morte para sempre? "
gameforge.locale.monster_chat_1037_2 = "Devias habituar-te à ideia de aceitar as trevas eternas... "
gameforge.locale.monster_chat_1037_3 = "Bem-vindo ao Inferno! "
gameforge.locale.monster_chat_1037_4 = "Grita o que quiseres! Aqui ninguém te consegue ouvir! "
gameforge.locale.monster_chat_1037_5 = "Se acreditas que me consegues matar dessa forma... És mais ingénuo do que eu pensava! "
gameforge.locale.monster_chat_1037_6 = "Belo ataque, mas não impedirá que a Morte te apanhe. "
gameforge.locale.monster_chat_1038_1 = "Isto é o Inferno! "
gameforge.locale.monster_chat_1038_2 = "Vou arrancar-te a pele dos ossos. "
gameforge.locale.monster_chat_1038_3 = "Quero ouvir as tuas lamurias antes de morreres. "
gameforge.locale.monster_chat_1038_4 = "Já deves saber que qualquer esperança será em vão, não? "
gameforge.locale.monster_chat_1038_5 = " "
gameforge.locale.monster_chat_1038_6 = "Não, não morras já. Primeiro quero saber quantos golpes da minha espada consegues aguentar... "
gameforge.locale.monster_chat_1039_1 = "Ah, um voluntário... "
gameforge.locale.monster_chat_1039_2 = "Achas que tens a mínima hipótese de escapar? "
gameforge.locale.monster_chat_1039_3 = "Não é o chão que se está a mexer... São as tuas pernas que tremem de medo! "
gameforge.locale.monster_chat_1039_4 = "Bem-vindo ao lugar onde encontrarás a tua Morte! "
gameforge.locale.monster_chat_1039_5 = "Consigo ver o pânico nos teus olhos brilhantes. "
gameforge.locale.monster_chat_1039_6 = "Oh, tão jovem e indefeso... "
gameforge.locale.monster_chat_1040_1 = "Observamos todos os teus movimentos... "
gameforge.locale.monster_chat_1040_2 = "Tens pressa? Queres assim tanto morrer? "
gameforge.locale.monster_chat_1040_3 = "A morte tem um lugar reservado para ti... "
gameforge.locale.monster_chat_1040_4 = "Consegues ouvir a minha campainha? Será a última coisa que vais ouvir! "
gameforge.locale.monster_chat_1040_5 = "Cheira-me a sangue, e bastante... Não sobreviverás muito mais tempo... "
gameforge.locale.monster_chat_1040_6 = "Podes lutar... Ou podes render-te imediatamente à morte... "
gameforge.locale.monster_chat_1041_1 = "Porque estás aqui se estás tão empenhado em manter-te vivo? "
gameforge.locale.monster_chat_1041_2 = "Ah, novos corpos para eu destruir! "
gameforge.locale.monster_chat_1041_3 = "Foi tua decisão vir até aqui, mortal! Agora terás de sofrer as consequências... "
gameforge.locale.monster_chat_1041_4 = "A nossa força não nos irá abandonar. Nós não morremos! "
gameforge.locale.monster_chat_1041_5 = "Não faz sentido atacares-nos! Estás condenado... "
gameforge.locale.monster_chat_1041_6 = "Talvez morras de exaustão... Ou medo... "
gameforge.locale.monster_chat_1091_1 = "Conseguiste chegar até mim... Irei levar-te até à Morte. "
gameforge.locale.monster_chat_1091_2 = "Sacrifica a tua alma e torna-te um de nós! "
gameforge.locale.monster_chat_1091_3 = "Olha para ti! Fraco e lento... "
gameforge.locale.monster_chat_1091_4 = "O teu corpo e alma são meus! "
gameforge.locale.monster_chat_1091_5 = "As tuas pernas estão a tremer! Admite, não aguentas isto... "
gameforge.locale.monster_chat_1091_6 = "Quanto tempo mais vais aguentar isto? "
gameforge.locale.monster_chat_1093_1 = "Quem poderia adivinhar que há algo pior que a morte? "
gameforge.locale.monster_chat_1093_2 = "Bem vindo, banquete da morte. "
gameforge.locale.monster_chat_1093_3 = "Vais pagar pela tua imprudência. Com o teu sangue e a tua alma. "
gameforge.locale.monster_chat_1093_4 = "Depois de destruir o teu corpo, a tua alma será minha! "
gameforge.locale.monster_chat_1093_5 = "Quanto tempo mais resistirás? "
gameforge.locale.monster_chat_1093_6 = "Tu divertes-me. O teu medo e precipitação divertem-me... "
gameforge.locale.monster_chat_11000_1 = "Todos os que se preocupam com o seu destino deviam ouvir! "
gameforge.locale.monster_chat_11000_10 = "Se queres controlar os poderes da Natureza e dos Raios recomendo a especialização Energia. "
gameforge.locale.monster_chat_11000_11 = "Cada Guerreiro é diferente na sua forma de lutar! Foca-te no fortalecimento do teu corpo! "
gameforge.locale.monster_chat_11000_12 = "A força de um Sura é mágica. Trabalha a tua Inteligência, Rapidez e Força Física! "
gameforge.locale.monster_chat_11000_13 = "Usa as habilidades dos Ninjas! Foca-te na velocidade e força física. "
gameforge.locale.monster_chat_11000_14 = "Os Xamãs usam feitiços de regeneração ajuda e combate. Inteligência é vital! "
gameforge.locale.monster_chat_11000_15 = "Não te esqueças de treinar a tua especialização! "
gameforge.locale.monster_chat_11000_16 = "Torna-te um aprendiz nosso caso querias treinar as tuas Habilidades! "
gameforge.locale.monster_chat_11000_17 = "Habilidades? Elas subirão para o nível de Mestre aleatoriamente entre os níveis 17 e 20! "
gameforge.locale.monster_chat_11000_18 = "Queres tornar-te um Grande Mestre? Então precisas de um Livro de Habilidades especial! "
gameforge.locale.monster_chat_11000_19 = "Assim que acabas de ler um Livro de Habilidades, ele desaparece! "
gameforge.locale.monster_chat_11000_2 = "Todos os heróis de nível 5 e abaixo, ouçam-me! "
gameforge.locale.monster_chat_11000_20 = "Queres ler outro Livro de Habilidades? Tens de esperar um dia. "
gameforge.locale.monster_chat_11000_21 = "Aumenta a tua Inteligência para melhorares as tuas habilidades de Sura! "
gameforge.locale.monster_chat_11000_22 = "Experimenta os grandes poderes de um Sura num combate! "
gameforge.locale.monster_chat_11000_23 = "Sabes onde se encontra o Templo da Seita Secreta? No centro do Vale Seungryong! "
gameforge.locale.monster_chat_11000_24 = "A Caverna das Aranhas reapareceu. A sua entrada encontra-se no Deserto Yongbi! "
gameforge.locale.monster_chat_11000_25 = "Termina as tuas missões e usa as recompensas em melhoramentos! "
gameforge.locale.monster_chat_11000_26 = "Pescar? Parece divertido! Já compraste uma Cana de Pesca e isco a um Pescador? "
gameforge.locale.monster_chat_11000_27 = "Queres um desafio maior? O Portal pode enviar-te para um local de caça apropriado para o teu nível! "
gameforge.locale.monster_chat_11000_28 = "Cuidado com jogadores de outros Reinos. Não sabes quando te poderão apanhar desprevenido! "
gameforge.locale.monster_chat_11000_29 = "Queres reiniciar o treino das tuas Habilidades? Vai à Idosa. Não te esqueças de levar Yang suficiente contigo! "
gameforge.locale.monster_chat_11000_3 = "Dirige-te ao Professor de Luta Corpo se quiseres ser conhecido como um grande Guerreiro ofensivo! "
gameforge.locale.monster_chat_11000_30 = "Almeja atingir o nível de Mestre Perfeito nas tuas Habilidades! Essa é a única forma de te tornares um verdadeiro herói! "
gameforge.locale.monster_chat_11000_31 = "Se te queres dirigir para outro local, procura as pessoas idosas pelo continente! "
gameforge.locale.monster_chat_11000_4 = "Se queres ser um mestre nas Habilidades de um Guerreiro vai e aprende com o mestre na Arte da Guerra! "
gameforge.locale.monster_chat_11000_5 = "Se te quiseres tornar um Sura mestre nas espadas mágicas escolhe a especialização Luta d'Armas. "
gameforge.locale.monster_chat_11000_6 = "Escolhe a especialização Magia Negra se quiseres ser um Sura hábil com os feitiços. "
gameforge.locale.monster_chat_11000_7 = "A Arte do Arco está reservada aos Ninjas de nível 5 e acima. "
gameforge.locale.monster_chat_11000_8 = "Escolhe a Arte Furtiva se quiseres subir na hierarquia secreta dos Ninjas! "
gameforge.locale.monster_chat_11000_9 = "Se quiseres controlar os combates como um guerreiro do Deus Dragão escolhe a especialização Força Dragão! "
