gameforge.locale.monster_chat_1105_1 = "Estar congelado é bom... para mim! "
gameforge.locale.monster_chat_1105_2 = "Tenho a certeza que a Montanha Sohan é o melhor lugar! "
gameforge.locale.monster_chat_1105_3 = "Que belo tempo. "
gameforge.locale.monster_chat_1105_4 = "No fim irás ser tão frio como eu... "
gameforge.locale.monster_chat_1105_5 = "Poderás viver para sempre, pelo menos até o gelo derreter. "
gameforge.locale.monster_chat_1105_6 = "Como achas que te sentirás quando te fores lentamente tornando num pedaço de gelo? "
gameforge.locale.monster_chat_1106_1 = "Humanos... aqui? "
gameforge.locale.monster_chat_1106_2 = "Cansado... Mas suficientemente desperto para ti... "
gameforge.locale.monster_chat_1106_3 = "Quem se passeia pela minha montanha?  "
gameforge.locale.monster_chat_1106_4 = "Tu não pertences aqui! "
gameforge.locale.monster_chat_1106_5 = "É melhor fugires daqui! "
gameforge.locale.monster_chat_1106_6 = "Um estranho... tão sozinho? "
gameforge.locale.monster_chat_1191_1 = "Já passou algum tempo desde que vimos um lutador experiente por aqui. "
gameforge.locale.monster_chat_1191_2 = "Não tenho mais nada para fazer além de esperar e polir armas... "
gameforge.locale.monster_chat_1191_3 = "Achas que devia trabalhar nesse aspecto? "
gameforge.locale.monster_chat_1191_4 = " "
gameforge.locale.monster_chat_1191_5 = "Falta algo. Não estou contente... "
gameforge.locale.monster_chat_1191_6 = "Vou pôr um fim rápido à tua dor - e será a última dor que irás sentir! "
gameforge.locale.monster_chat_1301_1 = "Um pescoço-longo...! "
gameforge.locale.monster_chat_1301_2 = "Vais ficar ainda mais pálido! "
gameforge.locale.monster_chat_1301_3 = "Queres desafiar-me? Está bem! "
gameforge.locale.monster_chat_1301_4 = "Atacaste-me! Não vais sobreviver! "
gameforge.locale.monster_chat_1301_5 = "És um humano fraco! Fraco! "
gameforge.locale.monster_chat_1301_6 = "Vamos ver quanto tempo te aguentas! "
gameforge.locale.monster_chat_1302_1 = "A tentar fugir? Nem penses nisso. "
gameforge.locale.monster_chat_1302_2 = "É isso mesmo... Aproxima-te! "
gameforge.locale.monster_chat_1302_3 = "A espada treme na minha mão... "
gameforge.locale.monster_chat_1302_4 = "Não sobreviverás a este combate! "
gameforge.locale.monster_chat_1302_5 = "Aqui vai a minha espada! "
gameforge.locale.monster_chat_1302_6 = "Doloroso, não é? "
gameforge.locale.monster_chat_1303_1 = "Mesmo a erva sobre a qual caminhas te pode matar. "
gameforge.locale.monster_chat_1303_2 = "Se fosse a ti teria cuidado... "
gameforge.locale.monster_chat_1303_3 = "É isso. Aproxima-te... "
gameforge.locale.monster_chat_1303_4 = "Atreves-te a aproximar-te? Bom... Terás de sofrer as consequências! "
gameforge.locale.monster_chat_1303_5 = "Está na hora de dizeres adeus ao mundo! "
gameforge.locale.monster_chat_1303_6 = "Já estás arrependido por teres vindo? "
gameforge.locale.monster_chat_1305_1 = "Quem conseguirá derrotar-me? "
gameforge.locale.monster_chat_1305_2 = "Tu? Tu queres lutar? "
gameforge.locale.monster_chat_1305_3 = "Ataca-me, se te atreveres! "
gameforge.locale.monster_chat_1305_4 = "Não estás a pensar em fugir, ou estás? "
gameforge.locale.monster_chat_1305_5 = "Subestimaste o alcance da minha espada. "
gameforge.locale.monster_chat_1305_6 = "Estás com medo? "
gameforge.locale.monster_chat_1901_1 = "Acho que irás morrer nas minhas mãos hoje. "
gameforge.locale.monster_chat_1901_2 = "Oh... Gosto de ti... "
gameforge.locale.monster_chat_1901_3 = "Ah, então queres trazer um pouco de variedade à minha vida? "
gameforge.locale.monster_chat_1901_4 = "Hey! Não te ajudará fugires como um cobarde. "
gameforge.locale.monster_chat_1901_5 = "Já te arrepiaste simplesmente de me ver? "
gameforge.locale.monster_chat_1901_6 = "Não queres gritar um pouco? "
gameforge.locale.monster_chat_20001_1 = "Pareces estar nervoso... Não te preocupes, nada irá explodir. "
gameforge.locale.monster_chat_20001_2 = "Às vezes só quero ir dar um passeio. "
gameforge.locale.monster_chat_20001_3 = "O tempo passa tão devagar... O meu neto já nem me vem visitar. "
gameforge.locale.monster_chat_20001_4 = "Quando envelheces não tens muita companhia. "
gameforge.locale.monster_chat_20001_5 = "Os meus dias já acabaram. "
gameforge.locale.monster_chat_20002_1 = "O sol está tão quente... "
gameforge.locale.monster_chat_20002_2 = "Belo tempo para secar roupa, não é? "
gameforge.locale.monster_chat_20002_3 = "Onde está o preguiçoso do meu marido? "
gameforge.locale.monster_chat_20002_4 = "Ele nem tem vergonha de os seus filhos conhecerem a sua preguiça! E intitula-se pai... "
gameforge.locale.monster_chat_20002_5 = "Livros, sempre livros! Ele não vai trabalhar e não traz Yang para casa. Passa o tempo todo a ler! "
gameforge.locale.monster_chat_20003_1 = "Olá pequeno, dormiste bem? "
gameforge.locale.monster_chat_20003_2 = "Meu anjinho. "
gameforge.locale.monster_chat_20003_3 = "És o meu raio de sol... "
gameforge.locale.monster_chat_20003_4 = "O que se passa? Tens fome? "
gameforge.locale.monster_chat_20003_5 = "É isso mesmo... dorme, meu pequeno. "
gameforge.locale.monster_chat_20004_1 = "É apenas mais um dia... "
gameforge.locale.monster_chat_20004_2 = "Onde irei arranjar algo para comer hoje? "
gameforge.locale.monster_chat_20004_3 = "É sempre a mesma coisa com estes guerreiros de espada. "
gameforge.locale.monster_chat_20004_4 = "Deixa ver.... Talvez devesse ir ao Restaurante do Octávio. "
gameforge.locale.monster_chat_20004_5 = "Oh, já agora, parece que a Guerra já terminou... "
gameforge.locale.monster_chat_20005_1 = "A alma de um Mestre Oleiro corre pelos seus dedos. "
gameforge.locale.monster_chat_20005_2 = "Desde que o Reino mergulhou no caos, existem combates por todo o lado. "
gameforge.locale.monster_chat_20005_3 = "Combates, combates por todo o lado. "
gameforge.locale.monster_chat_20005_4 = "Estou a rezar por um mundo calmo e pacífico. "
gameforge.locale.monster_chat_20005_5 = "Qual é a pressa? Tira uns segundos para relaxares e pensares. "
gameforge.locale.monster_chat_20006_1 = "Oh meu Deus! O que devo fazer? "
gameforge.locale.monster_chat_20006_2 = "Não encontro o meu irmão, e ele prometeu encontrar-se comigo aqui. "
gameforge.locale.monster_chat_20006_3 = "E agora tudo o que vejo são guerreiros armados com espadas. "
gameforge.locale.monster_chat_20006_4 = "Tenho saudades do meu irmão. "
gameforge.locale.monster_chat_20006_5 = "Alguém viu o meu irmão? "
gameforge.locale.monster_chat_20007_1 = "Hum... Essa carne é mesmo fresca! "
gameforge.locale.monster_chat_20007_2 = "Ainda bem que agora as pessoas comem mais carne. "
gameforge.locale.monster_chat_20007_3 = "O negócio corre bem, mas a carne ainda escasseia. "
gameforge.locale.monster_chat_20007_4 = "Quando receberemos a carne encomendada? "
gameforge.locale.monster_chat_20007_5 = "Se a carne não chegar a tempo teremos que partir à caça. "
gameforge.locale.monster_chat_20008_1 = "Espero que os clientes venham aos montes quando o tempo estiver bom... "
gameforge.locale.monster_chat_20008_2 = "Aquele desgraçado deve estar prestes a aparecer, e de certeza que irá querer algo para comer. "
gameforge.locale.monster_chat_20008_3 = "Pobre homem! "
gameforge.locale.monster_chat_20008_4 = "Outrora ele foi um grande guerreiro. "
gameforge.locale.monster_chat_20008_5 = "Porque será que ele já não luta? "
gameforge.locale.monster_chat_20009_1 = "Os jovens... Tão vivaços e alegres! "
gameforge.locale.monster_chat_20009_2 = "Os guerreiros ficam mais impressionantes quando empunham armas. "
gameforge.locale.monster_chat_20009_3 = "Hoje em dia todos têm uma mente assassina. "
gameforge.locale.monster_chat_20009_4 = "Controla o teu coração, foca os teus pensamentos e acalma a tua alma. "
gameforge.locale.monster_chat_20009_5 = "Não há uma receita infalível para a felicidade, mas existem dicas que podes seguir. "
gameforge.locale.monster_chat_20010_1 = "Este é um item muito útil! "
gameforge.locale.monster_chat_20010_2 = "Bens vindos de Reinos distantes! Preços extraordinários! "
gameforge.locale.monster_chat_20010_3 = "Aproximem-se todos! "
gameforge.locale.monster_chat_20010_4 = "Venham cá e comprem, pessoal! Não terão outra oportunidade! "
gameforge.locale.monster_chat_20010_5 = "Há demasiados bandidos por aqui. Eles deveriam ficar longe dos meus bens! "
gameforge.locale.monster_chat_20011_1 = "Nos últimos anos a terra tem estado muito seca. "
gameforge.locale.monster_chat_20011_2 = "A colheita de cereais já não é o que era. "
gameforge.locale.monster_chat_20011_3 = "Porque é que os campos já não se enchem de flores? "
gameforge.locale.monster_chat_20011_4 = "Os cereais são quebradiços e sabem a podre. "
gameforge.locale.monster_chat_20011_5 = "Isso é um mau sinal. A agricultura é a fundação das nossas vidas. "
gameforge.locale.monster_chat_20012_1 = "Bolos de arroz! Bolas de arroz deliciosos! "
gameforge.locale.monster_chat_20012_2 = "Vendo bolos de arroz deliciosos! "
gameforge.locale.monster_chat_20012_3 = "Sim, ultimamente o negócio tem estado muito mau. "
gameforge.locale.monster_chat_20012_4 = "Talvez devesse tentar noutra vila... "
gameforge.locale.monster_chat_20012_5 = "Mas há tantos predadores selvagens por aqui... "
gameforge.locale.monster_chat_20013_1 = "Em dias como este, estar num barco é a melhor coisa que te pode acontecer. "
gameforge.locale.monster_chat_20013_2 = "Está o tempo perfeito para a pesca! "
gameforge.locale.monster_chat_20013_3 = "Agora outro nó... Certo! Nenhum peixe irá escapar a este anzol! "
gameforge.locale.monster_chat_20013_4 = "Aquele estúpido urso estragou a minha Cana! "
gameforge.locale.monster_chat_20013_5 = "Devia arranjar as minhas redes de novo. "
gameforge.locale.monster_chat_20014_1 = "Huh! "
gameforge.locale.monster_chat_20014_2 = "Não te preocupes comigo. Esta é a minha maneira de ser. "
gameforge.locale.monster_chat_20014_3 = "Ha! "
gameforge.locale.monster_chat_20014_4 = "O que é que deveria fazer para eles... Oh, o que acabei de dizer? "
gameforge.locale.monster_chat_20014_5 = "Não sou suficientemente corajoso. "
gameforge.locale.monster_chat_20015_1 = "Este machado está ao meu serviço há 20 anos! "
gameforge.locale.monster_chat_20015_2 = "Ainda está afiado, mas já não tanto como antigamente. "
gameforge.locale.monster_chat_20015_3 = "Quero continuar a usá-lo, por isso precisa de estar afiado. "
gameforge.locale.monster_chat_20015_4 = "Mas para isso preciso de pedras com minerais especiais. "
gameforge.locale.monster_chat_20015_5 = "Onde as poderei arranjar? "
gameforge.locale.monster_chat_20016_1 = "Os clientes vêm aos montes. "
gameforge.locale.monster_chat_20016_2 = "Espero conseguir terminar tudo a tempo. "
gameforge.locale.monster_chat_20016_3 = "Martelada e mais martelada e sem me queixar... "
gameforge.locale.monster_chat_20016_4 = "É assim que um martelo deve soar! "
gameforge.locale.monster_chat_20016_5 = "Está muito calor aqui. "
gameforge.locale.monster_chat_20017_1 = "Não estou com humor para músicas. "
gameforge.locale.monster_chat_20017_2 = "O meu instrumento encontra-se em mau-estado. "
gameforge.locale.monster_chat_20017_3 = "Talvez devesse arranjar um novo instrumento? "
gameforge.locale.monster_chat_20017_4 = "O meu instrumento está velho e gasto... Adoro coisas antigas! "
gameforge.locale.monster_chat_20017_5 = "Preciso de encontrar alguém urgentemente para reparar as cordas partidas. "
gameforge.locale.monster_chat_20018_1 = "Estou muito ocupado! "
gameforge.locale.monster_chat_20018_2 = "Todos os dias recebemos novas pessoas feridas. "
gameforge.locale.monster_chat_20018_3 = "Não tenho tempo para assuntos particulares! "
gameforge.locale.monster_chat_20018_4 = "Além de que estamos a ficar sem medicamentos. "
gameforge.locale.monster_chat_20018_5 = "Precisamos de alguém que consiga obter ingredientes para o remédio. "
gameforge.locale.monster_chat_20019_1 = "Durante 30 anos vagueei pelas montanhas com o meu arco. "
gameforge.locale.monster_chat_20019_2 = "Com isto consegui sustentar a minha mulher e os meus três filhos. "
gameforge.locale.monster_chat_20019_3 = "É uma vida difícil, mas vale a pena. "
gameforge.locale.monster_chat_20019_4 = "Mas agora a corda partiu-se e não posso caçar. Não consigo sustentar a minha família assim! "
gameforge.locale.monster_chat_20019_5 = "Se não reparar este arco, terei de comprar um novo. Mas não tenho dinheiro para isso. "
gameforge.locale.monster_chat_20020_1 = "Hey! Para onde estás a olhar? "
gameforge.locale.monster_chat_20020_2 = "Fica longe daqui! "
gameforge.locale.monster_chat_20020_3 = "Durante quanto tempo mais terei de continuar escondido? "
gameforge.locale.monster_chat_20020_4 = "Não quero morrer sozinho e esquecido pelo mundo. "
gameforge.locale.monster_chat_20020_5 = "Alguém tem de me ajudar! "
gameforge.locale.monster_chat_20021_1 = "Este dia irá terminar como qualquer outro. "
gameforge.locale.monster_chat_20021_2 = "Que posso fazer agora? "
gameforge.locale.monster_chat_20021_3 = "Nunca pensei que isto fosse acabar assim. "
gameforge.locale.monster_chat_20021_4 = "Ainda não acredito que ele já não está entre nós. "
gameforge.locale.monster_chat_20021_5 = "Ele não merecia aquilo. "
gameforge.locale.monster_chat_20022_1 = "Mais um belo dia. "
gameforge.locale.monster_chat_20022_2 = "O marido da Ariyoung desapareceu. "
gameforge.locale.monster_chat_20022_3 = "Queria ajudar, mas esta teimosia... "
gameforge.locale.monster_chat_20022_4 = "Se ela tivesse casado comigo ela podia ter deixado a sua aborrecida vida para trás. "
gameforge.locale.monster_chat_20022_5 = "Talvez devesse ter expressado os meus sentimentos por ela de outra forma. "
gameforge.locale.monster_chat_20023_1 = "Oh não! Que irei fazer se a minha mulher descobrir? "
gameforge.locale.monster_chat_20023_2 = "Se eu pudesse ao menos viver da leitura de livros! "
gameforge.locale.monster_chat_20023_3 = "Cada livro absorve-te para o seu mundo misterioso. "
gameforge.locale.monster_chat_20023_4 = "Não posso viver assim para sempre, mas... "
gameforge.locale.monster_chat_20023_5 = "Gostaria de mudar, pela minha mulher, mas... "
gameforge.locale.monster_chat_20024_1 = "Vá lá, esforça-te um pouco! "
gameforge.locale.monster_chat_20024_2 = "Que estás a fazer? Não te podes despachar? "
gameforge.locale.monster_chat_20024_3 = "Imagina um guerreiro a erguer a sua espada e faz o mesmo! "
gameforge.locale.monster_chat_20024_4 = "Onde se meteu aquele tolo? "
gameforge.locale.monster_chat_20024_5 = "Queres tornar-te um homem? "
gameforge.locale.monster_chat_2091_1 = "Cortar, cortar... Cabelos, gargantas... "
gameforge.locale.monster_chat_2091_2 = "Tenho algo para ti: sono eterno! "
gameforge.locale.monster_chat_2091_3 = "Respira fundo. Não te preocupes, é apenas veneno! "
gameforge.locale.monster_chat_2091_4 = "Vou deixar apenas a tua cara! Será uma boa decoração... "
gameforge.locale.monster_chat_2091_5 = " "
gameforge.locale.monster_chat_2091_6 = "Enrolado em seda serás um bom aperitivo para mais logo... "
gameforge.locale.monster_chat_2092_1 = "Já não existem muitos de nós por aí... "
gameforge.locale.monster_chat_2092_2 = "Não são os seres de duas pernas que irão mandar aqui. "
gameforge.locale.monster_chat_2092_3 = "Oh! Parece que alguém foi capturado pela minha teia! "
gameforge.locale.monster_chat_2092_4 = "Sou uma criatura pacífica. Apenas não gosto que me irritem. "
gameforge.locale.monster_chat_2092_5 = "Isso é tudo o que consegues fazer? "
gameforge.locale.monster_chat_2092_6 = "Ninguém me consegue enfrentar! "
gameforge.locale.monster_chat_2104_1 = "Porque é que as pessoas são tão húmidas por dentro? Isso torna a minha espada ferrugenta! "
gameforge.locale.monster_chat_2104_2 = "O veneno do teu ferrão já secou? "
gameforge.locale.monster_chat_2104_3 = "Então e que tal essa luta? "
gameforge.locale.monster_chat_2104_4 = "Já estás exausto pelo calor? "
gameforge.locale.monster_chat_2104_5 = "Gosto de deixar pessoas para trás no deserto. "
gameforge.locale.monster_chat_2104_6 = "Coitado de ti, enterrado no deserto sem qualquer cerimónia... "
gameforge.locale.monster_chat_2105_1 = "Vou contar-te um segredo - detesto sujidade! "
gameforge.locale.monster_chat_2105_2 = "Está muito calor, não está? "
gameforge.locale.monster_chat_2105_3 = "Este lugar é perigoso. "
gameforge.locale.monster_chat_2105_4 = " "
gameforge.locale.monster_chat_2105_5 = "Vou acertar-te no meio dos olhos! "
gameforge.locale.monster_chat_2106_1 = "Parece que já tenho algo para matar. "
gameforge.locale.monster_chat_2106_2 = "Vejo os meus próximos espólios diante mim. "
gameforge.locale.monster_chat_2106_3 = "Gosto disso, gosto muito disso! "
gameforge.locale.monster_chat_2106_4 = "Aquele ali é bom e gordo. É meu! "
gameforge.locale.monster_chat_2106_5 = " "
gameforge.locale.monster_chat_2106_6 = "Adoro o cheiro do sangue. "
gameforge.locale.monster_chat_2107_1 = "Ultimamente não há muito para matar. "
gameforge.locale.monster_chat_2107_2 = "Hey! Estes são mesmo gordos! "
gameforge.locale.monster_chat_2107_3 = "O líder vai gostar disto... "
gameforge.locale.monster_chat_2107_4 = " "
gameforge.locale.monster_chat_2107_5 = "Vamos calçar os nossos sapatos de dança! "
gameforge.locale.monster_chat_2107_6 = "Esta flecha é só para ti! "
gameforge.locale.monster_chat_2108_1 = "Se eu apanhar um, vou... "
gameforge.locale.monster_chat_2108_2 = "Hey, o que se passa? "
gameforge.locale.monster_chat_2108_3 = "Achas que me irás derrotar facilmente? "
gameforge.locale.monster_chat_2108_4 = "Que mau para ti te teres cruzado comigo no deserto! "
gameforge.locale.monster_chat_2108_5 = "Ah, esta sensação! Irei desfrutar dela para sempre! "
gameforge.locale.monster_chat_2108_6 = "Obrigado! "
gameforge.locale.monster_chat_2191_1 = "Sinto o cheiro de humanos. Que cheiro delicioso! "
gameforge.locale.monster_chat_2191_2 = "É isso mesmo, chega-te aqui... "
gameforge.locale.monster_chat_2191_3 = " "
gameforge.locale.monster_chat_2191_4 = "Que sorte que te tenhas perdido por aqui! "
gameforge.locale.monster_chat_2191_5 = "Pára! "
gameforge.locale.monster_chat_2191_6 = "Vou comer a tua carne pedaço a pedaço. "
gameforge.locale.monster_chat_2202_1 = "Aproxima-te para que eu te possa queimar. "
gameforge.locale.monster_chat_2202_2 = "Sabes quanta dor um enorme calor pode provocar? "
gameforge.locale.monster_chat_2202_3 = "Talvez devesse arrefecer as minhas chamas com sangue. "
gameforge.locale.monster_chat_2202_4 = "O teu sangue... deixa-me prová-lo. "
gameforge.locale.monster_chat_2202_5 = "Não faz sentido resistires. "
gameforge.locale.monster_chat_2202_6 = "Torna-te como eu! Apenas chamas e calor. "
gameforge.locale.monster_chat_2204_1 = "Enquanto eu aqui estiver, ninguém passará! "
gameforge.locale.monster_chat_2204_2 = "Por alguma razão começas a irritar-me. "
gameforge.locale.monster_chat_2204_3 = " "
gameforge.locale.monster_chat_2204_4 = "Sabes onde estás? "
gameforge.locale.monster_chat_2204_5 = " "
gameforge.locale.monster_chat_2204_6 = "És um adversário patético! "
gameforge.locale.monster_chat_2205_1 = "Uma espada apenas tem utilidade nas mãos de um guerreiro. "
gameforge.locale.monster_chat_2205_2 = "Mantém a espada na tua mão. "
gameforge.locale.monster_chat_2205_3 = "A guerra aproxima-se. "
gameforge.locale.monster_chat_2205_4 = "E agora lutaremos até à morte! "
gameforge.locale.monster_chat_2205_5 = "Não vais conseguir continuar a resistir à morte! "
gameforge.locale.monster_chat_2205_6 = "Vais morrer lentamente. "
gameforge.locale.monster_chat_2206_1 = "A lava é o Elixir da Vida. "
gameforge.locale.monster_chat_2206_2 = "É a lava que me mantém vivo e com ela que morrerás! "
gameforge.locale.monster_chat_2206_3 = "Não é lindo, este mundo cheio de lava brilhante? "
gameforge.locale.monster_chat_2206_4 = "Atreves-te a incomodar-me? "
gameforge.locale.monster_chat_2206_5 = "Se amas a tua vida aconselho-te a desaparecer. "
gameforge.locale.monster_chat_2206_6 = "Vais arrepender-te de te cruzares comigo. "
gameforge.locale.monster_chat_2291_1 = "Está um belo dia. Tudo o que quero é um pouco de paz. "
gameforge.locale.monster_chat_2291_2 = "Nenhum lugar se compara a Doyyumhwan. "
gameforge.locale.monster_chat_2291_3 = "Um pouco de carne fresca seria uma mudança agradável. "
gameforge.locale.monster_chat_2291_4 = "Deves estar à procura da Morte, caso contrário não me perturbarias. "
gameforge.locale.monster_chat_2291_5 = "Já te arrependes de ter vindo até aqui? "
gameforge.locale.monster_chat_2291_6 = "É demasiado tarde para fugires. Este é o teu destino. "
gameforge.locale.monster_chat_101_1 = "Wuff. "
gameforge.locale.monster_chat_301_1 = "Estes dias tensos deixam-me nervosa. "
gameforge.locale.monster_chat_301_2 = "Há dois dias atrás uma pequena rapariga foi atacada por animais selvagens. "
gameforge.locale.monster_chat_301_3 = "Os animais selvagens estão a tornar-se mais agressivos. Devíamos matar tantos quanto pudermos. "
gameforge.locale.monster_chat_301_4 = "Não estou de bom humor. Não te deverias meter comigo. "
gameforge.locale.monster_chat_301_5 = "O último guerreiro que morreu pelas minhas mãos implorou pela sua vida. "
gameforge.locale.monster_chat_302_1 = "Alguém tem de fazer algo em relação a estas Pedras. "
gameforge.locale.monster_chat_302_2 = "Não confio na paz da manhã. "
gameforge.locale.monster_chat_302_3 = "A tensão no ar está a deixar-me desconfortável. "
gameforge.locale.monster_chat_302_4 = "Estas pontas de flecha foram muito bem forjadas. "
gameforge.locale.monster_chat_302_5 = "Se eu estivesse no teu lugar teria cuidado. A flecha está apontada ao teu coração. "
gameforge.locale.monster_chat_302_6 = "Devia ir matar algumas criaturas. "
gameforge.locale.monster_chat_303_1 = "Hoje em dia é preciso termos um bom treino de combate. "
gameforge.locale.monster_chat_303_2 = "Os soldados já não conseguem controlar o caos no Reino. "
gameforge.locale.monster_chat_303_3 = "Porque estás a olhar para mim dessa forma? "
gameforge.locale.monster_chat_303_4 = "Os cobardes têm pouca hipótese de sobreviver. "
gameforge.locale.monster_chat_303_5 = "Não te sobrestimes. "
gameforge.locale.monster_chat_303_6 = "Parece que alguém te treinou bem. "
gameforge.locale.monster_chat_304_1 = "Até os meus antepassados se conseguiram impor em combate nestes territórios. "
gameforge.locale.monster_chat_304_2 = "Aqui tens de estar atento e ser habilidoso. "
gameforge.locale.monster_chat_304_3 = "Nem penses em gastar o meu tempo. "
gameforge.locale.monster_chat_304_4 = "Então, como é saber que a tua vida está em perigo? "
gameforge.locale.monster_chat_304_5 = "Já queres desistir? "
gameforge.locale.monster_chat_304_6 = "Prepara-te para sofrer. "
gameforge.locale.monster_chat_305_1 = "Oh! Essa espada é fantástica. "
gameforge.locale.monster_chat_305_2 = "Se persistente e aumenta o teu poder! "
gameforge.locale.monster_chat_305_3 = "Achas que consegues ser tão forte como eu? "
gameforge.locale.monster_chat_305_4 = "Ok. Vamos a coisas sérias. "
gameforge.locale.monster_chat_305_5 = "Oh! Sou uma simples e fraca mulher! "
gameforge.locale.monster_chat_305_6 = "Pareces cansado. Porque não desistes já? Hehe. "
gameforge.locale.monster_chat_306_1 = "O que é que Serangyi irá fazer hoje? "
gameforge.locale.monster_chat_306_2 = "Estou tão aborrecida. Brinca comigo! "
gameforge.locale.monster_chat_306_3 = "Devo confessar que gosto mais do Sura. "
gameforge.locale.monster_chat_306_4 = "Hey! E que tal uma luta? "
gameforge.locale.monster_chat_306_5 = "Não me subestimes só por ser uma mulher! "
gameforge.locale.monster_chat_306_6 = "A dor só durará um momento. "
gameforge.locale.monster_chat_307_1 = "Quero sair daqui a todo o custo. "
gameforge.locale.monster_chat_307_2 = "Quão seguras são as casas dos habitantes da vila? "
gameforge.locale.monster_chat_307_3 = "Raparigas, calem-se! "
gameforge.locale.monster_chat_307_4 = "Deixa-me matar-te com um só golpe. "
gameforge.locale.monster_chat_307_5 = "Nem penses em fugir! "
gameforge.locale.monster_chat_307_6 = "Então, porque não começamos? "
gameforge.locale.monster_chat_394_1 = "Não se esqueçam: todos os homens são inimigos. "
gameforge.locale.monster_chat_394_2 = "Se alguém tiver uma cara simpática podemos abrir uma excepção... "
gameforge.locale.monster_chat_394_3 = "O quê? Queres lutar? "
gameforge.locale.monster_chat_394_4 = "Não demorará muito, mas irá doer. "
gameforge.locale.monster_chat_394_5 = "Oh, realmente pareces estar em dor. "
gameforge.locale.monster_chat_394_6 = "Adeus! Vai para o inferno! "
gameforge.locale.monster_chat_401_1 = "Então... O que fazes na Jura de Cristal? "
gameforge.locale.monster_chat_401_2 = "Onde vais? "
gameforge.locale.monster_chat_401_3 = "Hey! Queres lutar? "
gameforge.locale.monster_chat_401_4 = "Hehe! Estás a esquivar-te dos meus ataques. "
gameforge.locale.monster_chat_401_5 = "Pensas que consegues fugir de mim? "
gameforge.locale.monster_chat_401_6 = "Cá vai o meu golpe! "
gameforge.locale.monster_chat_402_1 = "Meu Deus, estou tão cansado! "
gameforge.locale.monster_chat_402_2 = "Hey, bela armadura! Foi muito cara? "
gameforge.locale.monster_chat_402_3 = "Pelo menos temos bom tempo. "
gameforge.locale.monster_chat_402_4 = "Oh! Tens andado a treinar bastante! Excelente! "
gameforge.locale.monster_chat_402_5 = "O que achas das minhas habilidades? Boas, não? Hehe. "
gameforge.locale.monster_chat_402_6 = "Que se passa? Oh, a minha rapidez incrível está a irritar-te! "
gameforge.locale.monster_chat_403_1 = "Porque não vou caçar? "
gameforge.locale.monster_chat_403_2 = "Ainda ninguém atacou a nossa vila hoje. "
gameforge.locale.monster_chat_403_3 = "Se queres lutar, luta, maldito! "
gameforge.locale.monster_chat_403_4 = "Hehe! És só um principiante. "
gameforge.locale.monster_chat_403_5 = "Onde te deverei acertar primeiro... No braço ou na perna? "
gameforge.locale.monster_chat_403_6 = "Aqui vai a minha flecha! "
gameforge.locale.monster_chat_404_1 = "Brevemente terei de ir à vila e treinar. "
gameforge.locale.monster_chat_404_2 = "Sinto-me muito tenso. "
gameforge.locale.monster_chat_404_3 = "O que será o jantar hoje? "
gameforge.locale.monster_chat_404_4 = "Estou a observar-te, por isso grita tanto quanto quiseres. "
gameforge.locale.monster_chat_404_5 = "Já esperaste muito? E que tal ergueres essa espada?! "
gameforge.locale.monster_chat_404_6 = "A tua espada faz um som óptimo! "
gameforge.locale.monster_chat_405_1 = "A minha espada está a postos! "
gameforge.locale.monster_chat_405_2 = "Hmm, vamos ver: Quem é que esta espada vai matar primeiro... "
gameforge.locale.monster_chat_405_3 = "Hey! Para onde estás a olhar? "
gameforge.locale.monster_chat_405_4 = "Primeiro o teu estômago! "
gameforge.locale.monster_chat_405_5 = "Gostaria de te usar como bainha. "
gameforge.locale.monster_chat_405_6 = "Gostaria de te usar como bainha. "
gameforge.locale.monster_chat_406_1 = "Estou a morrer de fome. "
gameforge.locale.monster_chat_406_2 = "Há alguma coisa que se coma? "
gameforge.locale.monster_chat_406_3 = "Porque estou ainda com tanta fome depois de comer tanto? "
gameforge.locale.monster_chat_406_4 = "Argh! Morre! "
gameforge.locale.monster_chat_406_5 = "O meu estômago irá decidir este combate! "
gameforge.locale.monster_chat_406_6 = "É a escolha entre o Paraíso e o Inferno! "
gameforge.locale.monster_chat_491_1 = "Não aguento mais! O meu corpo está em pulgas. "
gameforge.locale.monster_chat_491_2 = "Oh sim! Já vejo os porcos! "
gameforge.locale.monster_chat_491_3 = "Já no chão? "
gameforge.locale.monster_chat_491_4 = "Vou acabar contigo com apenas um golpe! "
gameforge.locale.monster_chat_491_5 = "Isto vai ficar na minha memória! "
gameforge.locale.monster_chat_491_6 = "Morre! "
gameforge.locale.monster_chat_492_1 = "O meu corpo está mole. "
gameforge.locale.monster_chat_492_2 = "Porque é que não treino mais? "
gameforge.locale.monster_chat_492_3 = "Com medo, hein? "
gameforge.locale.monster_chat_492_4 = "Ah, mais uma vítima para a minha arma poderosa. "
gameforge.locale.monster_chat_492_5 = "Percebes o meu padrão de ataque? Não? Pior para ti! "
gameforge.locale.monster_chat_492_6 = "Até os mais pequenos cortes influenciam a tua vida. Especialmente se forem feitos por uma moca! "
gameforge.locale.monster_chat_493_1 = "Raios, estas mãos não dão jeito nenhum. "
gameforge.locale.monster_chat_493_2 = "Para onde estás a olhar? Nunca viste ninguém com um punho de aço? "
gameforge.locale.monster_chat_493_3 = "Vocês enervam-me realmente. "
gameforge.locale.monster_chat_493_4 = "Queres que te espanque? Se não, começa a correr! Percebeste? "
gameforge.locale.monster_chat_493_5 = "Vou mostrar-te o meu melhor golpe! "
gameforge.locale.monster_chat_493_6 = "Pára de olhar para a minha mão! "
gameforge.locale.monster_chat_494_1 = "Hey, tu aí! Queres lutar? Então chega-te aqui! "
gameforge.locale.monster_chat_494_2 = "Acho que sou de cortar a respiração. "
gameforge.locale.monster_chat_494_3 = "Porque não planeamos qualquer coisa? "
gameforge.locale.monster_chat_494_4 = "Não! Anda não te autorizei a ir embora! Luta! "
gameforge.locale.monster_chat_494_5 = "Aí tens! "
gameforge.locale.monster_chat_494_6 = "Estás feito ao bife! "
gameforge.locale.monster_chat_501_1 = "Hey, fica aqui! Brinca comigo! "
gameforge.locale.monster_chat_501_2 = "Podia criar algo engraçado com os teus ossos... "
gameforge.locale.monster_chat_501_3 = "Ainda não treinámos hoje. "
gameforge.locale.monster_chat_501_4 = "Que bela armadura! Quero-a para mim! "
gameforge.locale.monster_chat_501_5 = "A partir de agora será mais difícil ignorares os meus ataques. "
gameforge.locale.monster_chat_501_6 = "Ha, ha, ha! Aposto que não estavas à espera disto! "
gameforge.locale.monster_chat_502_1 = "A beleza não é tudo. "
gameforge.locale.monster_chat_502_2 = "Não fiques a olhar! Luta! "
gameforge.locale.monster_chat_502_3 = "Não gosto da forma como olhas para mim. Luta! "
gameforge.locale.monster_chat_502_4 = "Será uma honra morreres pela minha espada! "
gameforge.locale.monster_chat_502_5 = " "
gameforge.locale.monster_chat_502_6 = "Óptimo! "
gameforge.locale.monster_chat_503_1 = "Hey! Não toques! "
gameforge.locale.monster_chat_503_2 = "Parece que a corda do meu arco foi cortada. "
gameforge.locale.monster_chat_503_3 = "Raios, aqueles idiotas estiveram aqui! "
gameforge.locale.monster_chat_503_4 = "Nada mau... Mas vais morrer de qualquer maneira! "
gameforge.locale.monster_chat_503_5 = "Parece-te bem? Óptimo! "
gameforge.locale.monster_chat_503_6 = "Aqui vai a minha flecha! "
gameforge.locale.monster_chat_504_1 = "Venham cá todos! "
gameforge.locale.monster_chat_504_2 = "La, lala, estou a assobiar uma música... "
gameforge.locale.monster_chat_504_3 = "Que belo tempo! "
gameforge.locale.monster_chat_504_4 = " "
gameforge.locale.monster_chat_504_5 = "Óptimo! Gosto do teu espírito lutador! "
gameforge.locale.monster_chat_504_6 = "Venham cá! Venham todos a mim! "
gameforge.locale.monster_chat_601_1 = "Estou a morrer de fome! "
gameforge.locale.monster_chat_601_2 = "Sinto desejos de frutas. "
gameforge.locale.monster_chat_601_3 = "Bom, o peixe também serve. Ao ataque! "
gameforge.locale.monster_chat_601_4 = "Não fujas! Fica aqui! "
gameforge.locale.monster_chat_601_5 = "Ganhei. É óbvio! "
gameforge.locale.monster_chat_601_6 = "Vês? Falhaste! "
gameforge.locale.monster_chat_601_7 = "Devias ter cuidado! Haha! "
gameforge.locale.monster_chat_602_1 = "Onde estou? "
gameforge.locale.monster_chat_602_2 = "Que belo tempo. "
gameforge.locale.monster_chat_602_3 = "Vieste caçar-me? Absurdo! "
gameforge.locale.monster_chat_602_4 = "És a minha próxima vítima. "
gameforge.locale.monster_chat_602_5 = "Doloroso, não é? Bom, vai ser rápido. "
gameforge.locale.monster_chat_602_6 = "Vê o quão forte sou: consigo empunhar esta moca enorme! "
gameforge.locale.monster_chat_603_1 = "Aborrecido! "
gameforge.locale.monster_chat_603_2 = "O meu corpo está em pulgas. "
gameforge.locale.monster_chat_603_3 = "Vamos ver quem é mais rápido. "
gameforge.locale.monster_chat_603_4 = "Tu é que começaste. Por isso não me responsabilizes pela tua morte! "
gameforge.locale.monster_chat_603_5 = "Assim que aqui entras nunca mais sais! Não há escapatória! "
gameforge.locale.monster_chat_603_6 = "Este será o meu último golpe. Vou acabar contigo! "
gameforge.locale.monster_chat_604_1 = "Criei um novo veneno delicioso. Queres prová-lo? "
gameforge.locale.monster_chat_604_2 = "Quero ver como funciona! Toma! "
gameforge.locale.monster_chat_604_3 = "Hey, tu! Anda cá. Queres ser a minha cobaia? "
gameforge.locale.monster_chat_604_4 = "Vou envenenar-te! "
gameforge.locale.monster_chat_604_5 = "Oh! Óptimo! Uma cobaia! "
gameforge.locale.monster_chat_604_6 = "Não me divirto se desistires tão depressa! Anda lá, defende-te! "
gameforge.locale.monster_chat_631_1 = "Espera um pouco. Estou à procura de algo para caçar. "
gameforge.locale.monster_chat_631_2 = "Não te serve de nada seres tão lento. Mexe-te! "
gameforge.locale.monster_chat_631_3 = "Tenho tanta fome! "
gameforge.locale.monster_chat_631_4 = "Ainda bem que encontrei algo para caçar! "
gameforge.locale.monster_chat_631_5 = "Que toda a esperança de sobreviver desapareça! Não passarás por mim! "
gameforge.locale.monster_chat_631_6 = "Agora irás descobrir como é a verdadeira dor! "
gameforge.locale.monster_chat_632_1 = "Aborreciiiiiiiido! "
gameforge.locale.monster_chat_632_2 = "Há por aí algo que eu possa usar como isco? "
gameforge.locale.monster_chat_632_3 = "Vem e luta se quiseres! "
gameforge.locale.monster_chat_632_4 = "Queres ser um guerreiro? Então mostra-me o que vales! "
gameforge.locale.monster_chat_632_5 = " "
gameforge.locale.monster_chat_632_6 = "Foge e volta para a vila se quiseres viver! "
gameforge.locale.monster_chat_633_1 = "Morrer de fome é uma morte deplorável. "
gameforge.locale.monster_chat_633_2 = "Oh! Eles vêm direitos a nós! "
gameforge.locale.monster_chat_633_3 = "E que tal um duelo? "
gameforge.locale.monster_chat_633_4 = "Não me compares a esses malditos! "
gameforge.locale.monster_chat_633_5 = "Aqui vai o meu golpe trovão! Ha! "
gameforge.locale.monster_chat_633_6 = "Aqueles que fogem não são verdadeiros Guerreiros! "
gameforge.locale.monster_chat_634_1 = "Desta vez o veneno deverá ser mais eficiente! "
gameforge.locale.monster_chat_634_2 = "Quando tens fome, até os morcegos se tornam uma verdadeira delícia! "
gameforge.locale.monster_chat_634_3 = "E que tal um combate? "
gameforge.locale.monster_chat_634_4 = "Assim que o meu veneno penetrar no teu corpo não terás cura possível! "
gameforge.locale.monster_chat_634_5 = "A minha magia irá queimar tudo! Hehehe! "
gameforge.locale.monster_chat_634_6 = "Arde! Haha! "
gameforge.locale.monster_chat_635_1 = "Nunca te esqueças afiar a tua espada! O próximo combate pode ser mais cedo do que pensas. "
gameforge.locale.monster_chat_635_2 = "Sentes-te zonzo? "
gameforge.locale.monster_chat_635_3 = "Morre e acorda no Além! "
gameforge.locale.monster_chat_635_4 = "Derrotar-me? Achas mesmo? "
gameforge.locale.monster_chat_635_5 = "Luta como um homem! "
gameforge.locale.monster_chat_635_6 = "Vá lá, só mais um pouco. "
gameforge.locale.monster_chat_636_1 = "Ha, estou à espera de um bom combate. "
gameforge.locale.monster_chat_636_2 = "Combater é divertido, haha! "
gameforge.locale.monster_chat_636_3 = "Luta! Mais e mais! Sim! "
gameforge.locale.monster_chat_636_4 = "Ha, muito bem! Tenta-o de novo! "
gameforge.locale.monster_chat_636_5 = "Olha para a minha espada a girar! "
gameforge.locale.monster_chat_636_6 = "Vais arrepender-te de me ter acertado! "
gameforge.locale.monster_chat_636_7 = "Tão fraco! Que ridículo! "
gameforge.locale.monster_chat_637_1 = "Não quero ser interrompido. "
gameforge.locale.monster_chat_637_2 = "Vai dormir. Para sempre! "
gameforge.locale.monster_chat_637_3 = "Continuas a não querer ir dormir? "
gameforge.locale.monster_chat_637_4 = "Há tanto para fazer! Vamos lá terminar isto! "
gameforge.locale.monster_chat_637_5 = "Não fiques abismado com todo o meu poder! "
gameforge.locale.monster_chat_637_6 = "Vou pulverizar-te! "
gameforge.locale.monster_chat_691_1 = "Óptimo, adversários fortes duram mais! Defende-te! "
gameforge.locale.monster_chat_691_2 = "O que foi? Tens algum problema com o meu irmão? "
gameforge.locale.monster_chat_691_3 = "Não me compares a esse principiante! "
gameforge.locale.monster_chat_691_4 = "Ele não gosta de ti. Nem eu! "
gameforge.locale.monster_chat_691_5 = "Vamos lutar! "
gameforge.locale.monster_chat_691_6 = "Até à morte! "
gameforge.locale.monster_chat_701_1 = "Olha, uma borboleta! Tão bonita! "
gameforge.locale.monster_chat_701_2 = "Não vês a verdade sobre o Céu e Terra? "
gameforge.locale.monster_chat_701_3 = "Depois vai ser demasiado tarde! Tens de acreditar agora! "
gameforge.locale.monster_chat_701_4 = "Acredita na doutrina dos Esotéricos! "
gameforge.locale.monster_chat_701_5 = "Todos aqueles que duvidarem serão mortos! "
gameforge.locale.monster_chat_701_6 = "Obtém o conhecimento aceitando a morte! "
gameforge.locale.monster_chat_702_1 = "A verdade da crença Esotérica baseia-se na compreensão da ordem do céu. "
gameforge.locale.monster_chat_702_2 = "A filosofia da crença Esotérica é a ordem celestial! "
gameforge.locale.monster_chat_702_3 = "A verdade Esotérica! Verdade do Deus Tigre! Sabaha Sabaha! "
gameforge.locale.monster_chat_702_4 = "Atreves-te a duvidar da verdade sobre o Céu e a Terra? "
gameforge.locale.monster_chat_702_5 = " "
gameforge.locale.monster_chat_702_6 = "Sente as minhas garras! "
gameforge.locale.monster_chat_703_1 = "Quando a verdade do Deus Tigre for cantada por toda a eternidade todo o sofrimento acabará. "
gameforge.locale.monster_chat_703_2 = "A verdade da crença Esotérica baseia-se na compreensão da ordem do céu. "
gameforge.locale.monster_chat_703_3 = "Sabaha Sabaha! Envia-nos a tua sabedoria! "
gameforge.locale.monster_chat_703_4 = "Não duvides! "
gameforge.locale.monster_chat_703_5 = "Recebe o trovão do Céu! "
gameforge.locale.monster_chat_703_6 = "Golpe do Juízo Final! "
gameforge.locale.monster_chat_704_1 = "A verdade da crença Esotérica baseia-se na compreensão da ordem do céu. "
gameforge.locale.monster_chat_704_2 = "Verdade Esotérica! Verdade do Deus Tigre! Sabaha Sabaha! "
gameforge.locale.monster_chat_704_3 = "Pensas que sou um palhaço não é? Não o farás por muito tempo! "
gameforge.locale.monster_chat_704_4 = "O líder da Seita é um santo acima de qualquer coisa viva! "
gameforge.locale.monster_chat_704_5 = "Arde nas chamas do Inferno! "
gameforge.locale.monster_chat_704_6 = "Aposto que nunca viste um Diapasão tão grande, hein? "
gameforge.locale.monster_chat_705_1 = "Verdade Esotérica! Verdade do Deus Tigre! Sabaha Sabaha! "
gameforge.locale.monster_chat_705_2 = "A verdade da crença Esotérica baseia-se na compreensão da ordem do céu. "
gameforge.locale.monster_chat_705_3 = "Só porque sou mais baixo que o meu irmão não significa que possa aturar tudo! "
gameforge.locale.monster_chat_705_4 = "A escuridão e dor eternas esperam-te! "
gameforge.locale.monster_chat_705_5 = " "
gameforge.locale.monster_chat_705_6 = "Isto não é o fim. Pelo contrário: Só vai ficar melhor. "
gameforge.locale.monster_chat_706_1 = "Apenas pedimos paz e privacidade! Por favor! "
gameforge.locale.monster_chat_706_2 = "Se o medo se apoderar de ti, coloca a tua fé no Deus Tigre. "
gameforge.locale.monster_chat_706_3 = "A verdade da crença Esotérica é o caminho para o conhecimento do Céu. "
gameforge.locale.monster_chat_706_4 = "Vamos todos para o Inferno! "
gameforge.locale.monster_chat_706_5 = "Não te deixarei escapar! "
gameforge.locale.monster_chat_706_6 = "Irei dar-te tudo o que tenho. "
gameforge.locale.monster_chat_707_1 = "Imploro-te! Por favor salva-nos! "
gameforge.locale.monster_chat_707_2 = "A verdade da crença Esotérica baseia-se na compreensão da ordem do céu. "
gameforge.locale.monster_chat_707_3 = "Verdade Esotérica! Verdade do Deus Tigre! Sabaha Sabaha! "
gameforge.locale.monster_chat_707_4 = "Os portões para o Inferno estão escancarados. Entra! "
gameforge.locale.monster_chat_707_5 = "Estou a avisar-te: Não me obrigues a usar a tábua! "
gameforge.locale.monster_chat_707_6 = "Já prevejo a tua morte! "
gameforge.locale.monster_chat_791_1 = "Deixa-me ver: Como posso resumir a ordem do céu e da terra em poucas palavras? "
gameforge.locale.monster_chat_791_2 = "Ouço os cantos dos crentes. "
gameforge.locale.monster_chat_791_3 = "Está certo! Enche-te de remorsos e serás resgatado! "
gameforge.locale.monster_chat_791_4 = "Irás sofrer eternamente por continuares a questionar a nossa fé. "
gameforge.locale.monster_chat_791_5 = "Deixa-me ensinar-te. "
gameforge.locale.monster_chat_791_6 = "Não o vejas como dor - vê-o como uma lição. "
gameforge.locale.monster_chat_901_1 = "Posso não ser bonito, mas divirto-me bastante, hihihi! "
gameforge.locale.monster_chat_901_2 = "Tens de te tornar um de nós! "
gameforge.locale.monster_chat_901_3 = "Vem até nós, hihi! "
gameforge.locale.monster_chat_901_4 = "Haaa, haha! "
gameforge.locale.monster_chat_901_5 = "Aqui vamos nós! "
gameforge.locale.monster_chat_903_1 = "Isto é o Inferno! "
gameforge.locale.monster_chat_903_2 = "Ahhhhh! "
gameforge.locale.monster_chat_903_3 = "Não conseguimos fazer isto sozinhos! Não! "
gameforge.locale.monster_chat_903_4 = "Tu também terás de provar a dor! "
gameforge.locale.monster_chat_903_5 = "E que tal uma troca? Dás-me a tua Alma e eu dou-te o meu sofrimento! "
gameforge.locale.monster_chat_903_6 = "Dentro em breve encontrarás a paz eterna! "
gameforge.locale.monster_chat_904_1 = "A dor! A dor! "
gameforge.locale.monster_chat_904_2 = "Aargghh, raios! Os meus braços ardem! "
gameforge.locale.monster_chat_904_3 = "Porque temos de sofrer assim? O que fizemos para merecer isto? "
gameforge.locale.monster_chat_904_4 = "Sofrer desta misteriosa doença nem é assim tão mau! "
gameforge.locale.monster_chat_904_5 = " "
gameforge.locale.monster_chat_904_6 = "Ahhhh! "
gameforge.locale.monster_chat_905_1 = "Ahhhhh! "
gameforge.locale.monster_chat_905_2 = "Eu maaaaaaaato-te! "
gameforge.locale.monster_chat_905_3 = "Não te limites a olhar! Participa! "
gameforge.locale.monster_chat_905_4 = "Vamos ser amigos! "
gameforge.locale.monster_chat_905_5 = "Já estás infectado! "
gameforge.locale.monster_chat_905_6 = "Agora és meu amigo. "
gameforge.locale.monster_chat_906_1 = "Hihihi! "
gameforge.locale.monster_chat_906_2 = "Vem cá e espeta-te nas minhas flechas. "
gameforge.locale.monster_chat_906_3 = "Estás a começar a irritar-me... "
gameforge.locale.monster_chat_906_4 = "Vou infectar-te com a nossa misteriosa doença! "
gameforge.locale.monster_chat_906_5 = "Que cheiro de criar água na boca! "
gameforge.locale.monster_chat_906_6 = "Morre! "
gameforge.locale.monster_chat_907_1 = "E se eu encontrar outra causa da doença... "
gameforge.locale.monster_chat_907_2 = "Ha, ha! Isto vai ser divertido! "
gameforge.locale.monster_chat_907_3 = "Hmm, chicotear ou agarrar...? "
gameforge.locale.monster_chat_907_4 = "Ahhhhh, siiiiiim! "
gameforge.locale.monster_chat_907_5 = "Deixa-me transmitir-te a doença! "
gameforge.locale.monster_chat_907_6 = "A causa desta misteriosa doença persiste! "
