gameforge.locale.monster_chat_1093_6 = "Ölüm arkandan gülümsüyor. (kahkaha) "
gameforge.locale.monster_chat_11000_1 = "Kendi gelişimleri için endişelenen kim varsa dinlesin! "
gameforge.locale.monster_chat_11000_10 = "Eğer doğanın ve şimşeklerin gücünü kontrol etmek istiyorsanız. İyileştirme eğitimini tavsiye ederim! "
gameforge.locale.monster_chat_11000_11 = "Her savaşçı farklı bir yolla savaşır! Bedenini güçlendirmeye odaklan! "
gameforge.locale.monster_chat_11000_12 = "Sura, gücünü büyüden alır. Zeka ve hızını geliştir ama beden gücünü ihmal etme! "
gameforge.locale.monster_chat_11000_13 = "Ninjanın yeteneklerini kullan! Hız ve beden gücüne çalış! "
gameforge.locale.monster_chat_11000_14 = "Şamanlar yenileme, destekleme ve uzak dövüş büyüsü kullanıyor. Zekalı olmak lazım! "
gameforge.locale.monster_chat_11000_15 = "Çıraklığını unutma! "
gameforge.locale.monster_chat_11000_16 = "Yeteneklerini geliştirmek istiyorsan, bir eğitim dalının çıraklığına katıl! "
gameforge.locale.monster_chat_11000_17 = "Yetenekler? 17 ve 21 seviyeleri arasında ustalık seviyesine erişirsin! "
gameforge.locale.monster_chat_11000_18 = "Grand Master gücüne erişmek ister misin? O zaman, özel beceri kitaplarına ihtiyacın var! "
gameforge.locale.monster_chat_11000_19 = "Bir defa yetenek kitabını okudun mu, kitap yok olur! "
gameforge.locale.monster_chat_11000_2 = "5. seviyenin altındaki tüm Savaşçılar, dinleyin! "
gameforge.locale.monster_chat_11000_20 = "Bir beceri kitabı daha okumak ister misin? Öyleyse 1 gün bekle. "
gameforge.locale.monster_chat_11000_21 = "Sura yeteneğini geliştirmek için zekanı yükseltmen lâzım! "
gameforge.locale.monster_chat_11000_22 = "Deneyim, savaşta Sura'nın en büyük gücüdür. "
gameforge.locale.monster_chat_11000_23 = "Gizli mezhebin tapınağı nerede biliyor musun? Seungryong vadisinin tam ortasında! "
gameforge.locale.monster_chat_11000_24 = "Örümcek mağaraları tekrar göründü. Yongbi çölündeler! "
gameforge.locale.monster_chat_11000_25 = "Görevlerini bitir ve geliştirme ödülleri kazan! "
gameforge.locale.monster_chat_11000_26 = "Balıkçılık? Kulağa hoş geliyor! Balıkçıdan zaten bir olta ve kanca almamış mıydın? "
gameforge.locale.monster_chat_11000_27 = "Avlamak için güçlü birşey bulmak ister misin? Bir kapı bul! Avlanma bölgesine git ve yeteneğini geliştir! "
