gameforge.main_quest_lv93._330_say = "Merhaba! Sana ne getirebilirim? Samgyetang ister[ENTER]misin? Evin spesiyalitesidir ... "
gameforge.main_quest_lv93._340_say = "Octavio, lezziz yemeğin için gelmedim! Başka bir[ENTER]şeye ihtiyacım var: milenyum esansı. "
gameforge.main_quest_lv93._350_say = "Aman dikkat, sessiz konuşuz! Milenyum esansı ...[ENTER]Hayır olmaz. Sana bunu kim söyledi? Esansın[ENTER]verdiği güçle[ENTER]herkes başa çıkamaz. Bu riske girmek istemem. Ve[ENTER]söylediklerini başkası duymadan gitsen iyi olur. "
gameforge.main_quest_lv93._360_say = "Octavio, bekle biraz ve beni dinle! Beni Karanlık[ENTER]Ejder Kayası'ndan Yon-I Gezgini gönderdi. Esansa[ENTER]ihtiyacımız var. O olmadan karanlığın kölelerine[ENTER]karşı hiç şansımız yok. Bildiğimiz dünyanın sonu[ENTER]çok yakın. Onu ancak sen kurtarabilirsin. Lütfen! "
gameforge.main_quest_lv93._370_say = "Yon-I Gezgini diyorsun yani? Eski bir[ENTER]arkadaşımdır. İyi bir şişe şarap eşliğinde ne hoş[ENTER]sohbetler ettik. Düşünmem gerek. Yarın tekrar gel,[ENTER]o zaman sana cevabımı veririm. "
gameforge.main_quest_lv93._380_say = "Anlaştık, yarın tekrar gelirim. "
gameforge.main_quest_lv93._390_say = "Daha karar vermedim. Daha sonra tekrar gel. "
gameforge.main_quest_lv93._400_npcChat = "Milenyum Esansı "
gameforge.main_quest_lv93._410_say = "Octavio seninle konuşmak istiyor. "
gameforge.main_quest_lv93._420_say = "Bütün gece ricanı düşündüm. Büyük bir risk[ENTER]alıyorum. Ama doğru olanı yaptığımdan şüphem yok.[ENTER]Evet, sana yardım edeceğim. "
gameforge.main_quest_lv93._430_say = "Harika! "
gameforge.main_quest_lv93._440_say = "Ama stoklarımda kalmadı. Bana birkaç malzeme[ENTER]bulabilir misin? Sana esansı sonra veririm. "
gameforge.main_quest_lv93._450_say = "Tabii. Ne lazım? "
gameforge.main_quest_lv93._460_say = "Gango kökü, tue mantarı ve alp gülü eksik. Her[ENTER]birinden 200 adet topla. "
gameforge.main_quest_lv93._470_say = "Hemen yola çıkıyorum. Bunları nerede bulabilirim? "
gameforge.main_quest_lv93._480_say = "Bu bitkileri kıtanın her yerinde bulabilirsin. Sen[ENTER]yola çık, ben de hazırlıklara başlayım. "
gameforge.main_quest_lv93._490_sayTitle = "Milenyum Tarifi "
gameforge.main_quest_lv93._500_say = "Gango kökü, tue mantarı ve alp gülü topla. Her[ENTER]birinden 200 adet lazım. "
gameforge.main_quest_lv93._510 = "Bu bitkileri kıtanın her yerinde bulabilirsin. Sen[ENTER]yola çık, ben de hazırlıklara başlayım. "
gameforge.main_quest_lv93._520_say = "Harika, geri döndün. Üstelik her şey yanında.[ENTER]Öyleyse hemen iş başına geçiyorum. Beni rahatsız[ENTER]etme. Yarın yine bu saatlerde gel. Esans o zamana[ENTER]kadar bitmiş olmalı. "
gameforge.main_quest_lv93._530_say = "Tamam. Yarın görüşürüz. "
gameforge.main_quest_lv93._540_say = "İşin acele, anlıyorum. Ama biraz daha beklemen[ENTER]gerekecek. Biraz sonra tekrar gel. "
gameforge.main_quest_lv93._550_say = "Octavio seni arıyor. "
gameforge.main_quest_lv93._560_say = "Tam zamanında geldin: Milenyum esansı da tam şimdi[ENTER]bitti! Bunu Yon-I Gezgini'ne götürebilirsin. "
gameforge.main_quest_lv93._570_say = "Teşekkür ederim. "
gameforge.main_quest_lv93._580_sendLetter = "Milenyum Esansı "
gameforge.main_quest_lv93._590_say = "Yon-I Gezgini seni arıyor. "
gameforge.main_quest_lv93._600_say = "Milenyum esansını aldın demek![ENTER]Güçlendirici iksirim için tüm malzemeleri bulduk.[ENTER]Hazır olduğunda sana haber vereceğim. "
gameforge.main_quest_lv93._610_say = "Tamam. "
gameforge.main_quest_lv93._620_say = "Seviye 94'e yükseldiğin anda macera devam edecek. "
gameforge.main_quest_lv93._630_say = "%s: %d adet, %s: %d adet, %s: %d adet "
gameforge.main_quest_lv93._640_say = "İlgilenmem gereken bir restoranım var ve zamanım[ENTER]dar. Eksik tüm malzemeleri getir - ve acele et lütfen. "
gameforge.main_quest_lv93._650_1_notice = "Milenyum esansı için gereken tüm malzemeleri topladın. "
gameforge.main_quest_lv93._650_2_notice = "Octavio'ya geri dön. "
