gameforge.npc_talk._720_sayTitle = "Harang: "
gameforge.npc_talk._730_say = "Taurean'ı bekliyorum. Beraber oyun oynarız ve[ENTER]çok da kavga ederiz. Ne korkak ama! Eğer kendi[ENTER]isteğiyle gelmezse onu çok fena yapacağım! "
gameforge.npc_talk._740_npcChat = "Taurean senden korkuyor. "
